import { useState } from "react";

export default function GuessInput({ socket, roomId, playerId }) {
  const [letter, setLetter] = useState("");
  const [word, setWord] = useState("");

  const guessLetter = () => {
    if (!letter) return;
    socket.emit("guess-letter", { roomId, playerId, letter });
    setLetter("");
  };

  const guessWord = () => {
    if (!word) return;
    socket.emit("guess-word", { roomId, playerId, guess: word });
    setWord("");
  };

  return (
    <div>
      <div>
        <input
          placeholder="Letter"
          value={letter}
          onChange={(e) => setLetter(e.target.value)}
          maxLength={1}
        />
        <button onClick={guessLetter}>Guess Letter</button>
      </div>
      <div>
        <input
          placeholder="Full Word"
          value={word}
          onChange={(e) => setWord(e.target.value)}
        />
        <button onClick={guessWord}>Guess Word</button>
      </div>
    </div>
  );
}
