import React from 'react';

function PlayerList({ players, currentPlayerId, wordSetterId, currentTurnId }) {
  return (
    <div className="bg-gray-50 rounded-xl p-6">
      <h3 className="text-xl font-bold text-gray-800 mb-4">Players</h3>
      <div className="space-y-3">
        {players.map(player => (
          <div 
            key={player.id} 
            className={`bg-white rounded-lg p-4 border-2 transition-all ${
              player.id === currentPlayerId 
                ? 'border-indigo-500 shadow-lg' 
                : player.id === currentTurnId
                ? 'border-yellow-400 bg-yellow-50 shadow-lg'
                : 'border-gray-200'
            }`}
          >
            <div className="flex flex-col gap-2">
              <div className="flex items-center gap-2 flex-wrap">
                <span className="font-bold text-gray-800">{player.name}</span>
                {player.isHost && (
                  <span className="px-2 py-0.5 bg-yellow-400 text-yellow-900 rounded-full text-xs font-bold">
                    üëë Host
                  </span>
                )}
                {player.id === wordSetterId && (
                  <span className="px-2 py-0.5 bg-purple-400 text-purple-900 rounded-full text-xs font-bold">
                    üìù Setter
                  </span>
                )}
                {player.id === currentPlayerId && (
                  <span className="px-2 py-0.5 bg-indigo-500 text-white rounded-full text-xs font-bold">
                    You
                  </span>
                )}
              </div>
              <div className="flex gap-4 text-sm">
                <span className="font-semibold text-green-600">
                  üèÜ {player.score} pts
                </span>
                {player.id !== wordSetterId && player.strikes > 0 && (
                  <span className="font-semibold text-red-600">
                    ‚ùå {player.strikes}
                  </span>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default PlayerList;
