import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { createRoom } from "../utils/api";

export default function Home() {
  const [name, setName] = useState("");
  const [roomId, setRoomId] = useState("");
  const navigate = useNavigate();

  const handleCreate = async () => {
    if (!name) return alert("Enter your name");
    const data = await createRoom({ hostName: name, totalRounds: 3, turnDuration: 30, maxStrikes: 6 });
    navigate(`/room/${data.roomId}`, { state: { playerName: name, playerId: data.hostId } });
  };

  const handleJoin = () => {
    if (!name || !roomId) return alert("Enter name and room ID");
    navigate(`/room/${roomId}`, { state: { playerName: name } });
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Multiplayer Hangman</h1>
      <input placeholder="Your Name" value={name} onChange={e => setName(e.target.value)} />
      <div className="mt-4">
        <button onClick={handleCreate}>Create Room</button>
      </div>
      <div className="mt-4">
        <input placeholder="Room ID" value={roomId} onChange={e => setRoomId(e.target.value)} />
        <button onClick={handleJoin}>Join Room</button>
      </div>
    </div>
  );
}
