import React, { useState, useEffect, useRef, useCallback } from "react";
import { FormControl, Input, InputLabel, Typography } from "@mui/material"; // updated
import Letters from "./Letters";
import Timer from "./Timer";
import "../css/Game.scss";
import { socket } from "../modules";


const figureMapping = [
  "none.png",
  "head.png",
  "body.png",
  "left_leg.png",
  "right_leg.png",
  "left_arm.png",
  "right_arm.png",
  "hair.png",
  "eyes.png",
  "nose.png",
  "mouth.png",
];

function Game({ gameState, setGameState, username, roomID, mute }) {
  const [word, setWord] = useState("");
  const [source, setSource] = useState("");
  const [showScore, setShowScore] = useState(false);
  const [change, setChange] = useState("");
  const audioRef = useRef(null);

  const gameHandler = useCallback(
    (newState) => {
      setGameState({ ...newState });
      setShowScore(true);
      setTimeout(() => setShowScore(false), 2000);
    },
    [setGameState]
  );

  const updateSong = (src) => {
    setSource(src);
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.load();
      audioRef.current.play().catch(() => {});
    }
  };

  const handleStatus = useCallback(
    (info) => {
      let newURL = "";
      let message = "";

      if (info.status === "timer") {
        newURL = `${process.env.REACT_APP_SERVER}/audio/timer.mp3`;
        message = `${username} ran out of time`;
        setChange("-5");
      } else if (info.status === "correct") {
        newURL = `${process.env.REACT_APP_SERVER}/audio/correct.mp3`;
        message = `${username} guessed ${info.guess}`;
        setChange("+15");
      } else if (info.status === "incorrect") {
        newURL = `${process.env.REACT_APP_SERVER}/audio/wrong.mp3`;
        message = `${username} guessed ${info.guess}`;
        setChange("-5");
      } else if (info.status === "win") {
        message = `${username} completed the word!`;
        setChange("+30");
      }

      updateSong(newURL);

      socket.emit("chat", {
        roomID,
        user: info.status,
        message,
        effects: true,
      });
    },
    [username, roomID]
  );

  useEffect(() => {
    socket.on("update", gameHandler);
    socket.on("status", handleStatus);
    return () => {
      socket.off("update", gameHandler);
      socket.off("status", handleStatus);
    };
  }, [gameHandler, handleStatus]);

  const validateGuess = () => {
    const field = document.getElementById("guess");
    if (!field) return;

    if (gameState.guessedWords.includes(field.value)) {
      field.setCustomValidity(`${field.value} has already been guessed`);
    } else if (!/^[^\s]+(\s+[^\s]+)*$/.test(field.value)) {
      field.setCustomValidity("Guess cannot have leading or trailing spaces");
    } else if (!/^[-\sa-zA-Z]+$/.test(field.value)) {
      field.setCustomValidity("Only alphabetic characters, spaces, and dashes allowed");
    } else if (field.value.length < 2) {
      field.setCustomValidity("Guess must have at least two characters");
    } else {
      field.setCustomValidity("");
    }
  };

  const makeGuess = (entity) => {
    const guessState = { ...gameState, curGuess: entity };
    socket.emit("guess", { roomID, gameState: guessState });
  };

  const onFormSubmit = (e) => {
    e.preventDefault();
    makeGuess(word);
    setWord("");
  };

  const onLetterClick = (e) => {
    e.preventDefault();
    setWord("");
    makeGuess(e.currentTarget.value);
  };

  const getScore = (player) => {
    const win = 30;
    const right = 15;
    const wrong = -5;
    const miss = -5;

    return (
      win * gameState.wins[player] +
      right * gameState.right[player] +
      wrong * gameState.wrong[player] +
      miss * gameState.misses[player]
    );
  };

  let prevGuesser = gameState.players.indexOf(gameState.guesser);

  do {
    prevGuesser =
      (((prevGuesser - 1) % gameState.players.length) +
        gameState.players.length) %
      gameState.players.length;
  } while (gameState.players[prevGuesser] === gameState.hanger);

  const mode = gameState.rotation === "robin" ? "Round Robin" : "King of the Hill";

  return (
    <>
      <div className="players">
        <h2>Active Players:</h2>

        {gameState.players.map((player) => (
          <Typography
            variant="h5"
            style={{ color: player === gameState.guesser ? "blue" : "black" }}
            key={player}
          >
            {username === player ? "*" : ""}
            {player} {getScore(player)}{" "}
            {showScore && player === gameState.players[prevGuesser] && (
              <span
                className="change"
                style={{ color: change.startsWith("+") ? "green" : "red" }}
              >
                {change}
              </span>
            )}
          </Typography>
        ))}

        <br />
        <Typography variant="h5">{`Hanger: ${gameState.hanger}`}</Typography>
        <Typography variant="h5">{`Mode: ${mode}`}</Typography>
      </div>

      <div className="game-container">
        {/* Audio FX */}
        {username === gameState.players[prevGuesser] && source && (
          <audio
            id="guessAudio"
            autoPlay
            onEnded={() => setSource("")}
            muted={mute}
            ref={audioRef}
          >
            <source src={source} />
          </audio>
        )}

        {username && (
          <div>
            <div className="top-container">
              <Typography variant="h4" paragraph>
                Round {gameState.round} of {gameState.numRounds}
              </Typography>

              {username === gameState.guesser && gameState.time && (
                <div className="timer">
                  <Timer gameState={gameState} makeGuess={makeGuess} />
                </div>
              )}
            </div>

            {username === gameState.hanger && (
              <Typography variant="h5" style={{ wordWrap: "break-word" }}>
                {`Word(s): ${gameState.word}`}
              </Typography>
            )}

            <Typography variant="h6">
              {`Category: ${gameState.category}`} <br />
              {`Guesses Remaining: ${gameState.lives - gameState.numIncorrect}`}
            </Typography>

            <Typography variant="h3" className="word">
              {gameState.guessedWord}
            </Typography>

            <img
              className="drawing"
              src={`/images/${figureMapping[gameState.numIncorrect]}`}
              alt="Hangman Progress"
            />

            {username !== gameState.hanger && (
              <>
                <Letters
                  onClick={onLetterClick}
                  disabled={gameState.guesser !== username}
                  guessedLetters={gameState.guessedLetters}
                />
                <br />

                <form onSubmit={onFormSubmit}>
                  <FormControl>
                    <InputLabel htmlFor="guess">Word</InputLabel>
                    <Input
                      type="text"
                      value={word}
                      onChange={(e) => setWord(e.target.value)}
                      id="guess"
                      name="guess"
                      inputProps={{ maxLength: 50, minLength: 2 }}
                      onInput={validateGuess}
                      disabled={gameState.guesser !== username}
                      required
                    />
                  </FormControl>
                </form>
              </>
            )}

            <br />
          </div>
        )}
      </div>
    </>
  );
}

export default Game;
