import React, { useState } from 'react';

const SERVER_URL = 'http://localhost:5000';

function CreateRoom({ onRoomCreated, onBack }) {
  const [playerName, setPlayerName] = useState('');
  const [lives, setLives] = useState(6);
  const [rounds, setRounds] = useState(5);
  const [guessTime, setGuessTime] = useState('30000');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleCreateRoom = async (e) => {
    e.preventDefault();
    
    if (!playerName.trim()) {
      setError('Please enter your name');
      return;
    }

    if (lives < 6 || lives > 10) {
      setError('Lives must be between 6 and 10');
      return;
    }

    if (rounds < 1) {
      setError('Rounds must be at least 1');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const settings = {
        lives: parseInt(lives),
        rounds: parseInt(rounds),
        guessTime: guessTime
      };

      const response = await fetch(`${SERVER_URL}/api/create-room`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          hostName: playerName.trim(),
          settings
        }),
      });

      const data = await response.json();
      
      if (response.ok) {
        onRoomCreated(data);
      } else {
        setError('Failed to create room');
      }
    } catch (err) {
      setError('Failed to connect to server');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500 flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        <div className="bg-white rounded-3xl shadow-2xl p-8 relative">
          <button 
            onClick={onBack}
            className="absolute top-6 left-6 text-indigo-600 hover:text-indigo-800 font-semibold transition-colors"
          >
            ‚Üê Back
          </button>
          
          <h2 className="text-3xl font-bold text-center text-gray-800 mb-8 mt-4">Create Room</h2>
          
          <form onSubmit={handleCreateRoom} className="space-y-6">
            <div>
              <label htmlFor="playerName" className="block text-sm font-semibold text-gray-700 mb-2">
                Your Name
              </label>
              <input
                type="text"
                id="playerName"
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
                placeholder="Enter your name"
                maxLength={20}
                disabled={loading}
                className="w-full px-4 py-3 border-2 border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all disabled:bg-gray-100 disabled:cursor-not-allowed"
              />
            </div>

            <div className="bg-gray-50 rounded-xl p-6 space-y-5">
              <h3 className="text-lg font-bold text-gray-800 mb-4">Room Settings</h3>
              
              <div>
                <label htmlFor="lives" className="block text-sm font-semibold text-gray-700 mb-2">
                  Number of Lives (6-10)
                </label>
                <input
                  type="number"
                  id="lives"
                  value={lives}
                  onChange={(e) => setLives(e.target.value)}
                  min={6}
                  max={10}
                  disabled={loading}
                  className="w-full px-4 py-3 border-2 border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all disabled:bg-gray-100 disabled:cursor-not-allowed"
                />
              </div>

              <div>
                <label htmlFor="rounds" className="block text-sm font-semibold text-gray-700 mb-2">
                  Number of Rounds
                </label>
                <input
                  type="number"
                  id="rounds"
                  value={rounds}
                  onChange={(e) => setRounds(e.target.value)}
                  min={1}
                  max={20}
                  disabled={loading}
                  className="w-full px-4 py-3 border-2 border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all disabled:bg-gray-100 disabled:cursor-not-allowed"
                />
              </div>

              <div>
                <label htmlFor="guessTime" className="block text-sm font-semibold text-gray-700 mb-2">
                  Guess Time Per Turn
                </label>
                <select
                  id="guessTime"
                  value={guessTime}
                  onChange={(e) => setGuessTime(e.target.value)}
                  disabled={loading}
                  className="w-full px-4 py-3 border-2 border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all disabled:bg-gray-100 disabled:cursor-not-allowed bg-white"
                >
                  <option value="30000">30 seconds</option>
                  <option value="60000">60 seconds</option>
                  <option value="90000">90 seconds</option>
                  <option value="unlimited">Unlimited</option>
                </select>
              </div>
            </div>

            {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-xl text-center">
                {error}
              </div>
            )}

            <button 
              type="submit" 
              disabled={loading}
              className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-4 rounded-xl transition-all transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none shadow-lg"
            >
              {loading ? 'Creating...' : 'Create Room'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default CreateRoom;
