import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function CreateRoom({ socket }) {
  const [name, setName] = useState("");
  const [roomSettings, setRoomSettings] = useState({
    totalRounds: 5,
    turnDuration: 30,
    maxStrikes: 6,
    allowFullWordGuess: true,
    chatEnabled: true,
    allowSpectators: true
  });
  const navigate = useNavigate();

  const handleCreateRoom = () => {
    if (!name.trim()) return alert("Enter your name");
    socket.emit("createRoom", { name, settings: roomSettings }, (roomId) => {
      navigate(`/room/${roomId}?name=${name}`);
    });
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-500 to-pink-500">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md">
        <h1 className="text-2xl font-bold mb-6 text-center text-gray-700">Create Hangman Room</h1>
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="w-full mb-4 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-400"
        />
        {/* Optionally, add inputs for settings */}
        <button
          onClick={handleCreateRoom}
          className="w-full bg-purple-500 hover:bg-purple-600 text-white py-2 rounded-lg transition"
        >
          Create Room
        </button>
      </div>
    </div>
  );
}
