import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

export default function Home() {
  const navigate = useNavigate();

  // feedback form state
  const [form, setForm] = useState({ name: "", email: "", feedback: "" });
  const [status, setStatus] = useState("SEND");
  const timerRef = useRef(null);

  // join modal state
  const [joinOpen, setJoinOpen] = useState(false);
  const [joinCode, setJoinCode] = useState("");
  const [joinError, setJoinError] = useState("");

  // feedback modal state
  const [showFeedback, setShowFeedback] = useState(false);

  // NAVIGATION FIXES (works like your second version)
  const handleCreate = () => {
    navigate("/create");
  };

  const handleOpenJoin = () => {
    setJoinCode("");
    setJoinError("");
    setJoinOpen(true);
  };

  const handleJoinSubmit = () => {
    const code = joinCode.trim();
    if (!code) {
      setJoinError("Please enter a room code");
      return;
    }
    navigate(`/${code}`);
  };

  const handleFeedbackSubmit = () => {
    if (!form.name || !form.email || !form.feedback) return;

    if (timerRef.current) clearTimeout(timerRef.current);

    setStatus("Sending...");

    // Simulated API
    setTimeout(() => {
      setForm({ name: "", email: "", feedback: "" });
      setStatus("Sent!");

      timerRef.current = setTimeout(() => setStatus("SEND"), 4000);
    }, 1000);
  };

  return (
    <div className="min-h-screen bg-black text-white">
      {/* Header */}
      <header className="border-b border-gray-800 px-10 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <div className="text-cyan-400">
              <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M14 10l-2 1m0 0l-2-1m2 1v2.5M20 7l-2 1m2-1l-2-1m2 1v2.5M14 4l-2-1-2 1M4 7l2-1M4 7l2 1M4 7v2.5M12 21l-2-1m2 1l2-1m-2 1v-2.5M6 18l-2-1v-2.5M18 18l2-1v-2.5"
                />
              </svg>
            </div>
            <h2 className="text-white text-lg font-bold">Hangman Multiplayer</h2>
          </div>

          <div className="hidden md:flex items-center gap-8">
            <a className="text-white text-sm font-medium hover:text-cyan-400 transition-colors cursor-pointer">About</a>
            <a className="text-white text-sm font-medium hover:text-cyan-400 transition-colors cursor-pointer">Leaderboards</a>
            <button className="rounded-full h-10 px-4 bg-gray-800 text-white text-sm font-bold hover:bg-gray-700 transition-colors">
              Log In
            </button>
          </div>
        </div>
      </header>

      {/* Main */}
      <main className="max-w-6xl mx-auto px-20 py-8">

        {/* Hero */}
        <div className="mt-10">
          <div className="flex flex-col gap-8 px-4 py-10 md:flex-row md:items-center">
            <div className="flex-1 text-center md:text-left">
              <h1 className="text-white text-6xl font-black leading-tight">
                The Ultimate Word Challenge.{" "}
                <span className="text-cyan-400">Together.</span>
              </h1>

              <h2 className="text-gray-400 text-lg mt-3">
                Create a private game, invite your friends, and guess the puzzle before it's too late.
              </h2>

              <div className="flex flex-col sm:flex-row gap-3 justify-center md:justify-start mt-6">
                <button
                  onClick={handleCreate}
                  className="rounded-full h-12 px-5 bg-cyan-500 text-black text-base font-bold hover:bg-cyan-400 transition-colors"
                >
                  Create New Game
                </button>

                <button
                  onClick={handleOpenJoin}
                  className="rounded-full h-12 px-5 bg-gray-800 text-cyan-400 text-base font-bold hover:bg-gray-700 transition-colors"
                >
                  Join with Code
                </button>
              </div>
            </div>

            {/* Hero image */}
            <div className="w-full md:w-2/5 mt-6 md:mt-0 flex items-center justify-center">
              <div
                className="w-full aspect-square bg-center bg-no-repeat bg-contain rounded-2xl"
                style={{
                  backgroundImage:
                    'url("https://images.unsplash.com/photo-1581090122109-c18218a55543?q=80&w=600&auto=format&fit=crop")',
                }}
              />
            </div>
          </div>
        </div>

        {/* Steps */}
        <section className="mt-24">
          <h2 className="text-white text-3xl font-bold text-center pb-3 pt-5">
            Simple to Learn, Fun to Master
          </h2>

          <div className="grid gap-4 p-4 grid-cols-1 sm:grid-cols-3 mt-8">
            {/* Step 1 */}
            <div className="flex flex-col gap-4 rounded-lg border border-gray-800 bg-gray-900 p-6 items-center text-center hover:border-cyan-500 transition-all">
              <div className="text-cyan-400 p-3 bg-gray-800 rounded-full">
                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v3m0 0v3m0-3h3m-3 0H9m12 0a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <h3 className="text-white text-lg font-bold">1. Start a Game</h3>
              <p className="text-gray-400 text-sm">Create a new game and share the code.</p>
            </div>

            {/* Step 2 */}
            <div className="flex flex-col gap-4 rounded-lg border border-gray-800 bg-gray-900 p-6 items-center text-center hover:border-cyan-500 transition-all">
              <div className="text-cyan-400 p-3 bg-gray-800 rounded-full">
                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6V4m0 2a2 2 0 100 4m0-4a2 2 0 110 4m-6 8a2 2 0 100-4m0 4a2 2 0 110-4m0 4v2m0-6V4m6 6v10m6-2a2 2 0 100-4m0 4a2 2 0 110-4m0 4v2m0-6V4" />
                </svg>
              </div>
              <h3 className="text-white text-lg font-bold">2. Guess Letters</h3>
              <p className="text-gray-400 text-sm">Take turns guessing letters.</p>
            </div>

            {/* Step 3 */}
            <div className="flex flex-col gap-4 rounded-lg border border-gray-800 bg-gray-900 p-6 items-center text-center hover:border-cyan-500 transition-all">
              <div className="text-cyan-400 p-3 bg-gray-800 rounded-full">
                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4" />
                </svg>
              </div>
              <h3 className="text-white text-lg font-bold">3. Win Together</h3>
              <p className="text-gray-400 text-sm">Solve the puzzle before the hangman is complete.</p>
            </div>
          </div>
        </section>

        {/* Why you'll love it */}
        <section className="mt-24">
          <div className="text-center mb-6">
            <h2 className="text-white text-3xl font-bold">Why You'll Love It</h2>
            <p className="text-gray-400 max-w-2xl mx-auto mt-3">
              Discover features that make Hangman Multiplayer amazing.
            </p>
          </div>

          <div className="grid gap-4 grid-cols-1 sm:grid-cols-3 mt-8">
            {/* Card 1 */}
            <div className="flex flex-col gap-4 rounded-lg border border-gray-800 bg-gray-900 p-6 hover:border-cyan-500 transition-all">
              <div className="text-cyan-400">
                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6" />
                </svg>
              </div>
              <h3 className="text-white text-lg font-bold">Custom Word Lists</h3>
              <p className="text-gray-400 text-sm">Use your own words for fun or challenge.</p>
            </div>

            {/* Card 2 */}
            <div className="flex flex-col gap-4 rounded-lg border border-gray-800 bg-gray-900 p-6 hover:border-cyan-500 transition-all">
              <div className="text-cyan-400">
                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2" />
                </svg>
              </div>
              <h3 className="text-white text-lg font-bold">Play with Friends</h3>
              <p className="text-gray-400 text-sm">Invite anyone with a simple code.</p>
            </div>

            {/* Card 3 */}
            <div className="flex flex-col gap-4 rounded-lg border border-gray-800 bg-gray-900 p-6 hover:border-cyan-500 transition-all">
              <div className="text-cyan-400">
                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6" />
                </svg>
              </div>
              <h3 className="text-white text-lg font-bold">Track Your Stats</h3>
              <p className="text-gray-400 text-sm">Compare your skills & progress.</p>
            </div>
          </div>
        </section>
      </main>

      {/* Footer */}
      <footer className="mt-16 border-t border-gray-800 py-8 px-10">
        <div className="flex flex-col sm:flex-row items-center justify-between gap-6 max-w-6xl mx-auto">
          <div className="flex items-center gap-4">
            <a className="text-gray-400 text-sm hover:text-white transition-colors cursor-pointer">About</a>
            <a className="text-gray-400 text-sm hover:text-white transition-colors cursor-pointer">Terms</a>
            <a className="text-gray-400 text-sm hover:text-white transition-colors cursor-pointer">Privacy</a>
          </div>

          <button
            onClick={() => setShowFeedback(true)}
            className="text-cyan-400 font-semibold text-sm hover:underline"
          >
            Got Feedback?
          </button>
        </div>
      </footer>

      {/* Join Modal */}
      {joinOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/80 p-4">
          <div className="w-full max-w-md bg-gray-900 rounded-xl p-6 border border-gray-800">
            <h3 className="text-lg font-bold mb-3">Join a Game</h3>
            <div>
              <input
                className="w-full p-3 rounded-md mb-3 bg-black border border-gray-800 focus:border-cyan-500 focus:outline-none transition-colors"
                placeholder="Enter room code"
                value={joinCode}
                onChange={(e) => setJoinCode(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && handleJoinSubmit()}
              />
              {joinError && <div className="text-red-400 mb-2">{joinError}</div>}
              <div className="flex items-center justify-end gap-3">
                <button
                  onClick={() => setJoinOpen(false)}
                  className="px-4 py-2 rounded-md border border-gray-800 hover:bg-gray-800 transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={handleJoinSubmit}
                  className="px-4 py-2 rounded-md bg-cyan-500 text-black font-bold hover:bg-cyan-400 transition-colors"
                >
                  Join
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Feedback FAB */}
      <div className="fixed right-6 bottom-6 z-40">
        <button
          onClick={() => setShowFeedback(true)}
          className="rounded-full px-4 py-3 bg-gray-900 text-white border border-gray-800 hover:bg-gray-800 transition-colors"
        >
          Feedback
        </button>
      </div>

      {/* Feedback Modal */}
      {showFeedback && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/80 p-4">
          <div className="w-full max-w-md bg-gray-900 rounded-xl p-6 border border-gray-800">
            <h3 className="text-lg font-bold mb-3">Send Feedback</h3>

            <div>
              <input
                className="w-full p-3 rounded-md mb-3 bg-black border border-gray-800 focus:border-cyan-500 focus:outline-none transition-colors"
                placeholder="Your Name"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
              />

              <input
                type="email"
                className="w-full p-3 rounded-md mb-3 bg-black border border-gray-800 focus:border-cyan-500 focus:outline-none transition-colors"
                placeholder="Email"
                value={form.email}
                onChange={(e) => setForm({ ...form, email: e.target.value })}
              />

              <textarea
                className="w-full p-3 rounded-md mb-3 bg-black border border-gray-800 focus:border-cyan-500 focus:outline-none transition-colors resize-none"
                placeholder="Your Feedback"
                rows="4"
                value={form.feedback}
                onChange={(e) => setForm({ ...form, feedback: e.target.value })}
              />

              <div className="text-sm text-gray-300 mb-2">{status}</div>

              <div className="flex items-center justify-end gap-3">
                <button
                  onClick={() => setShowFeedback(false)}
                  className="px-4 py-2 rounded-md border border-gray-800 hover:bg-gray-800 transition-colors"
                >
                  Cancel
                </button>

                <button
                  onClick={handleFeedbackSubmit}
                  className="px-4 py-2 rounded-md bg-cyan-500 text-black font-bold hover:bg-cyan-400 transition-colors"
                >
                  Send
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
