import React, { useState } from "react";
import {
  AppBar,
  Typography,
  ThemeProvider,
  CssBaseline,
  createTheme,  // changed from createMuiTheme
} from "@mui/material"; // updated path

import VolumeUpIcon from "@mui/icons-material/VolumeUp"; // updated path
import VolumeOffIcon from "@mui/icons-material/VolumeOff"; // updated path

import Create from "./Create";
import Room from "./Room";
import "../css/Main.scss";
import ReactGA from "react-ga4";
import { BrowserRouter, Routes, Route } from "react-router-dom"; // v6 change


const theme = createMuiTheme({
  typography: {
    fontFamily: ["Yeon Sung", "Sans Serif"].join(","),
    h3: {
      letterSpacing: "2px",
    },
  },
  palette: {
    primary: {
      main: "#2994ff",
    },
    background: {
      default: "#c2e0ff",
    },
  },
});

const key = process.env.REACT_APP_ANALYTICS_KEY;
if (key) {
  ReactGA.initialize(key);
}

const Main = () => {
  const [username, setUsername] = useState("");
  const [mute, setMute] = useState(true);

  return (
    <>
      <ThemeProvider theme={theme}>
        <CssBaseline />

        <AppBar position="static" className="menu">
          <Typography variant="h5">
            <a href="/">Hangmanonline.io</a>
          </Typography>

          <div className="volume" style={{ marginLeft: "10px" }}>
            {!mute && (
              <VolumeUpIcon
                onClick={() => setMute(!mute)}
                fontSize="large"
              />
            )}
            {mute && (
              <VolumeOffIcon
                onClick={() => setMute(!mute)}
                fontSize="large"
              />
            )}
          </div>
        </AppBar>

        <div className="container">
          <BrowserRouter>
            <Route exact path="/">
              <Create setUser={setUsername} user={username} />
            </Route>

            <Route path="/:roomID">
              <Room username={username} mute={mute} />
            </Route>
          </BrowserRouter>
        </div>
      </ThemeProvider>
    </>
  );
};

export default Main;
