import React from 'react';

const WordDisplay = ({ revealed, guessedLetters }) => {
  return (
    <div className="mb-8">
      <div className="flex flex-wrap justify-center gap-2 mb-6">
        {revealed?.split('').map((char, i) => (
          <div
            key={i}
            className="w-12 h-16 flex items-center justify-center text-3xl font-bold border-b-4 border-gray-300"
          >
            {char === '_' ? (
              <span className="text-gray-300">_</span>
            ) : (
              <span className="text-gray-800 animate-bounce">{char}</span>
            )}
          </div>
        ))}
      </div>

      <div className="bg-gray-50 rounded-lg p-4">
        <p className="text-sm text-gray-600 mb-2 font-medium">Guessed Letters:</p>
        <div className="flex flex-wrap gap-2">
          {guessedLetters && guessedLetters.length > 0 ? (
            guessedLetters.map((letter, i) => (
              <span
                key={i}
                className="w-8 h-8 flex items-center justify-center bg-gray-200 text-gray-700 rounded font-bold text-sm"
              >
                {letter}
              </span>
            ))
          ) : (
            <span className="text-gray-400 text-sm">None yet</span>
          )}
        </div>
      </div>
    </div>
  );
};

export default WordDisplay;
