// src/pages/Home.jsx
import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Home() {
  const navigate = useNavigate();

  // feedback form state
  const [form, setForm] = useState({ name: "", email: "", feedback: "" });
 
  const timerRef = useRef(null);

  // join modal state
  const [joinOpen, setJoinOpen] = useState(false);
  const [joinCode, setJoinCode] = useState("");
  const [joinError, setJoinError] = useState("");

  const handleCreate = () => {
    navigate("/create");
  };

  const handleOpenJoin = () => {
    setJoinCode("");
    setJoinError("");
    setJoinOpen(true);
  };

  const handleJoinSubmit = (e) => {
    e.preventDefault();
    const code = (joinCode || "").trim();
    if (!code) {
      setJoinError("Please enter a room code");
      return;
    }
    // navigate to room (assumes your router accepts /:roomID)
    navigate(`/${encodeURIComponent(code)}`);
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    if (timerRef.current) clearTimeout(timerRef.current);

    setStatus("Sending...");
    try {
      await axios.post(`${process.env.REACT_APP_SERVER}/feedback/`, { data: form });
      setForm({ name: "", email: "", feedback: "" });
      setStatus("Sent!");
    } catch (err) {
      console.error("Feedback failed", err);
      setStatus("Failed");
    }

    timerRef.current = setTimeout(() => setStatus("SEND"), 4000);
  };

  return (
    <div className="min-h-screen bg-background-light dark:bg-background-dark font-display text-gray-900 dark:text-gray-200">
      <div className="relative w-full overflow-x-hidden">
        <div className="flex flex-col min-h-screen">
          {/* Header */}
          <header className="flex items-center justify-between border-b border-solid border-white/10 px-4 sm:px-10 py-4">
            <div className="flex items-center gap-4">
              <div className="text-secondary">
                <span className="material-symbols-outlined !text-3xl">stadia_controller</span>
              </div>
              <h2 className="text-white text-lg font-bold leading-tight tracking-[-0.015em]">Hangman Multiplayer</h2>
            </div>

            <div className="hidden md:flex items-center gap-8">
              <div className="flex items-center gap-9">
                <a className="text-white text-sm font-medium hover:text-secondary transition-colors" href="#about">About</a>
                <a className="text-white text-sm font-medium hover:text-secondary transition-colors" href="#leaderboards">Leaderboards</a>
              </div>

              <button
                onClick={() => window.location.href = "/login"}
                className="rounded-full h-10 px-4 bg-white/10 text-white text-sm font-bold hover:bg-white/20 transition-colors"
              >
                Log In
              </button>
            </div>

            <button className="md:hidden flex items-center justify-center p-2 rounded-full bg-white/10 text-white">
              <span className="material-symbols-outlined">menu</span>
            </button>
          </header>

          {/* Main */}
          <main className="flex flex-col gap-12 sm:gap-16 md:gap-24 max-w-[1200px] mx-auto px-4 sm:px-8 md:px-20 lg:px-40 py-8 w-full">
            {/* Hero */}
            <div className="mt-10">
              <div className="flex flex-col gap-8 px-4 py-10 md:flex-row md:items-center">
                <div className="flex-1 text-center md:text-left">
                  <h1 className="text-white text-4xl font-black leading-tight md:text-6xl">
                    The Ultimate Word Challenge. <span className="text-primary">Together.</span>
                  </h1>
                  <h2 className="text-gray-400 text-base md:text-lg mt-3">
                    Create a private game, invite your friends, and guess the puzzle before it's too late.
                  </h2>

                  <div className="flex flex-col sm:flex-row flex-wrap gap-3 justify-center md:justify-start mt-6">
                    <button
                      onClick={handleCreate}
                      className="rounded-full h-12 px-5 bg-primary text-white text-base font-bold hover:bg-primary/90 transition-colors"
                    >
                      Create New Game
                    </button>

                    <button
                      onClick={handleOpenJoin}
                      className="rounded-full h-12 px-5 bg-secondary/20 text-secondary text-base font-bold hover:bg-secondary/30 transition-colors"
                    >
                      Join with Code
                    </button>
                  </div>
                </div>

                <div className="w-full md:w-2/5 mt-6 md:mt-0 flex items-center justify-center">
                  <div
                    className="w-full aspect-square bg-center bg-no-repeat bg-contain"
                    style={{
                      backgroundImage:
                        'url("https://images.unsplash.com/photo-1581090122109-c18218a55543?q=80&w=600&auto=format&fit=crop")',
                    }}
                    aria-hidden
                  />
                </div>
              </div>
            </div>

            {/* Simple to Learn section */}
            <section>
              <h2 className="text-white text-2xl sm:text-3xl font-bold text-center pb-3 pt-5">Simple to Learn, Fun to Master</h2>
              <div className="grid gap-4 p-4 grid-cols-1 sm:grid-cols-3">
                <div className="flex flex-col gap-4 rounded-lg border border-white/10 bg-white/5 p-6 items-center text-center hover:border-secondary/50 hover:bg-secondary/10 transition-all">
                  <div className="text-secondary p-3 bg-secondary/10 rounded-full">
                    <span className="material-symbols-outlined !text-3xl">add_circle</span>
                  </div>
                  <h3 className="text-white text-lg font-bold">1. Start a Game</h3>
                  <p className="text-gray-400 text-sm">Create a new game and share the unique code with your friends to get started.</p>
                </div>

                <div className="flex flex-col gap-4 rounded-lg border border-white/10 bg-white/5 p-6 items-center text-center hover:border-secondary/50 hover:bg-secondary/10 transition-all">
                  <div className="text-secondary p-3 bg-secondary/10 rounded-full">
                    <span className="material-symbols-outlined !text-3xl">keyboard</span>
                  </div>
                  <h3 className="text-white text-lg font-bold">2. Guess Letters</h3>
                  <p className="text-gray-400 text-sm">Take turns guessing letters to reveal the hidden word or phrase.</p>
                </div>

                <div className="flex flex-col gap-4 rounded-lg border border-white/10 bg-white/5 p-6 items-center text-center hover:border-secondary/50 hover:bg-secondary/10 transition-all">
                  <div className="text-secondary p-3 bg-secondary/10 rounded-full">
                    <span className="material-symbols-outlined !text-3xl">emoji_events</span>
                  </div>
                  <h3 className="text-white text-lg font-bold">3. Win Together</h3>
                  <p className="text-gray-400 text-sm">Solve the puzzle before the hangman is complete to claim victory.</p>
                </div>
              </div>
            </section>

            {/* Why you'll love it */}
            <section>
              <div className="text-center mb-6">
                <h2 className="text-white text-3xl font-bold">Why You'll Love It</h2>
                <p className="text-gray-400 max-w-[720px] mx-auto mt-3">Discover the features that make this the best place to play Hangman with friends and family online.</p>
              </div>

              <div className="grid gap-4 grid-cols-1 sm:grid-cols-3">
                <div className="flex flex-col gap-4 rounded-lg border border-white/10 bg-white/5 p-6">
                  <div className="text-primary">
                    <span className="material-symbols-outlined !text-3xl">list_alt</span>
                  </div>
                  <h3 className="text-white text-lg font-bold">Custom Word Lists</h3>
                  <p className="text-gray-400 text-sm">Use your own word lists for endless, personalized fun and inside jokes.</p>
                </div>

                <div className="flex flex-col gap-4 rounded-lg border border-white/10 bg-white/5 p-6">
                  <div className="text-primary">
                    <span className="material-symbols-outlined !text-3xl">group</span>
                  </div>
                  <h3 className="text-white text-lg font-bold">Play with Friends</h3>
                  <p className="text-gray-400 text-sm">Easily invite friends to a private game room with a simple shareable code.</p>
                </div>

                <div className="flex flex-col gap-4 rounded-lg border border-white/10 bg-white/5 p-6">
                  <div className="text-primary">
                    <span className="material-symbols-outlined !text-3xl">leaderboard</span>
                  </div>
                  <h3 className="text-white text-lg font-bold">Track Your Stats</h3>
                  <p className="text-gray-400 text-sm">Climb the leaderboards and see how your guessing skills stack up against others.</p>
                </div>
              </div>
            </section>
          </main>

          {/* Footer */}
          <footer className="mt-16 border-t border-white/10 py-8 px-4 sm:px-10">
            <div className="flex flex-col sm:flex-row items-center justify-between gap-6 max-w-[1200px] mx-auto">
              <div className="flex items-center gap-4">
                <a className="text-gray-400 text-sm hover:text-white transition-colors" href="#about">About</a>
                <a className="text-gray-400 text-sm hover:text-white transition-colors" href="#terms">Terms of Service</a>
                <a className="text-gray-400 text-sm hover:text-white transition-colors" href="#privacy">Privacy Policy</a>
              </div>

              <button className="text-primary font-semibold text-sm hover:underline">Got Feedback?</button>
            </div>
          </footer>
        </div>
      </div>

      {/* Join modal - simple inline modal */}
      {joinOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 p-4">
          <div className="w-full max-w-md bg-background-light dark:bg-[#0f1020] rounded-xl p-6">
            <h3 className="text-lg font-bold mb-3">Join a Game</h3>
            <form onSubmit={handleJoinSubmit}>
              <input
                className="w-full p-3 rounded-md mb-3 bg-white/5 border border-white/10"
                placeholder="Enter room code"
                value={joinCode}
                onChange={(e) => setJoinCode(e.target.value)}
              />
              {joinError && <div className="text-rose-400 mb-2">{joinError}</div>}
              <div className="flex items-center justify-end gap-3">
                <button
                  type="button"
                  onClick={() => setJoinOpen(false)}
                  className="px-4 py-2 rounded-md border border-white/10 bg-transparent"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 rounded-md bg-primary text-white"
                >
                  Join
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Feedback form anchored bottom-right (small floating) */}
      <div className="fixed right-6 bottom-6 z-40">
        <button
          onClick={() => document.getElementById("feedback-section")?.scrollIntoView({ behavior: "smooth" })}
          className="rounded-full px-4 py-3 bg-white/5 text-white hover:bg-white/10"
        >
          Feedback
        </button>
      </div>

      {/* anchor id for feedback scroll */}
      <div id="feedback-section" className="sr-only" />
    </div>
  );
}
