import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import Game from "./Game";
import Wait from "./Wait";
import Chat from "./Chat";
import NewWord from "./NewWord";
import axios from "axios";
import "../css/Room.scss";
import { socket } from "../modules/socket";
import {
  Box,
  Typography,
  Paper,
  List,
  ListItem,
  ListItemText,
} from "@mui/material";

function Room({ username, mute }) {
  const [gameState, setGameState] = useState(null);
  const [user, setUser] = useState(username || "");
  const { roomID } = useParams();
  const [err, setErr] = useState(false);

  // NEW UI STATES
  const [scorePopup, setScorePopup] = useState(null); 
  const [roundWinner, setRoundWinner] = useState(null);

  /** ---------------- PLAYER LEAVE ---------------- */
  const handleLeave = useCallback((newState) => {
    if (!newState || !Array.isArray(newState.players) || newState.players.length === 0) {
      setErr(true);
    } else {
      setGameState({ ...newState });
    }
  }, []);

  /** ---------------- INITIAL STATE FETCH ---------------- */
  useEffect(() => {
    const getGameState = async () => {
      try {
        const backendURL =
          process.env.REACT_APP_SERVER || "http://localhost:5000";
        const res = await axios.get(`${backendURL}/?roomID=${roomID}`);
        if (res.status === 200 && res.data && res.data.players) {
          setGameState(res.data);
        } else {
          setErr(true);
        }
      } catch {
        setErr(true);
      }
    };

    socket.on("leave", handleLeave);

    // NEW SCORE POPUP LISTENER
    socket.on("scoreEffect", ({ username, delta }) => {
      setScorePopup({ username, delta });
      setTimeout(() => setScorePopup(null), 1300);
    });

    // NEW ROUND WINNER ANNOUNCEMENT
    socket.on("roundWinner", ({ winner, word }) => {
      setRoundWinner({ winner, word });
      setTimeout(() => setRoundWinner(null), 4000);
    });

    getGameState();
    return () => {
      socket.off("leave", handleLeave);
      socket.off("scoreEffect");
      socket.off("roundWinner");
    };
  }, [roomID, handleLeave]);

  /** ---------------- LEAVE ON UNLOAD ---------------- */
  useEffect(() => {
    const cleanup = () => {
      if (user) socket.emit("leave", { user, roomID });
    };
    window.addEventListener("unload", cleanup);
    return () => window.removeEventListener("unload", cleanup);
  }, [user, roomID]);

  /** ---------------- CONTENT SWITCHER ---------------- */
  const renderContent = () => {
    if (err) return <Typography color="error">Room does not exist</Typography>;
    if (!gameState) return <Typography>Loading...</Typography>;

    if (!user || !gameState.gameStart) {
      return (
        <Wait
          user={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          setUser={setUser}
          mute={mute}
        />
      );
    }

    if (gameState.gameStart && gameState.category !== "") {
      return (
        <Game
          username={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          mute={mute}
        />
      );
    }

    if (gameState.category === "") {
      return (
        <NewWord
          gameState={gameState}
          setGameState={setGameState}
          user={user}
          roomID={roomID}
          mute={mute}
        />
      );
    }

    return <Typography>Loading...</Typography>;
  };

  const mode =
    gameState?.rotation === "robin"
      ? "Round Robin"
      : "King of the Hill";

  return (
    <Box sx={{ display: "flex", height: "95vh", width: "96vw", px: "8px", position: "relative" }}>

      {/* SCORE FLOATING POPUP */}
      {scorePopup && (
        <div className={`score-popup ${scorePopup.delta > 0 ? "positive" : "negative"}`}>
          {scorePopup.delta > 0 ? `+${scorePopup.delta}` : `${scorePopup.delta}`}
        </div>
      )}

      {/* ROUND WINNER POPUP */}
      {roundWinner && (
        <div className="round-winner-banner">
          <h2>{roundWinner.winner} WON THIS ROUND! ðŸŽ‰</h2>
          <p>The word was: <strong>{roundWinner.word}</strong></p>
        </div>
      )}

      {/* ---------------- LEFT PANEL ---------------- */}
      <Paper
        elevation={3}
        sx={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          p: 2,
          overflowY: "auto",
        }}
      >
        <Typography variant="h6" textAlign="center" sx={{ mb: 2 }}>
          ðŸŽ® Room Code: {roomID.toUpperCase()}
        </Typography>

        <Typography variant="subtitle1" fontWeight="bold">
          Players
        </Typography>
        <List dense>
          {gameState?.players?.map((player) => (
            <ListItem key={player}>
              <ListItemText
                primary={`${player} â€” ${
                  gameState.wins && gameState.right
                    ? gameState.wins[player] * 30 +
                      gameState.right[player] * 15 +
                      gameState.wrong[player] * -5 +
                      gameState.misses[player] * -5
                    : 0
                } pts`}
              />
            </ListItem>
          ))}
        </List>

        <Typography sx={{ mt: 2 }}>
          <b>Hanger:</b> {gameState?.hanger}
        </Typography>
        <Typography>
          <b>Mode:</b> {mode}
        </Typography>
        <Typography>
          <b>Round:</b> {gameState?.round}
        </Typography>
      </Paper>

      {/* ---------------- CENTER PANEL ---------------- */}
      <Box sx={{ flex: 2, display: "flex", flexDirection: "column", p: 2 }}>
        {renderContent()}
      </Box>

      {/* ---------------- RIGHT PANEL ---------------- */}
      <Paper elevation={3} sx={{ flex: 1, display: "flex", flexDirection: "column", p: 2 }}>
        <Typography variant="h6" textAlign="center" sx={{ mb: 2 }}>
          ðŸ’¬ Chat
        </Typography>
        {user && <Chat user={user} roomID={roomID} />}
      </Paper>
    </Box>
  );
}

export default Room;
