import React, { useState, useEffect, useRef, useCallback } from "react";
import {
  Box,
  Card,
  CardContent,
  Typography,
  FormGroup,
  TextField,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
  Button,
  Divider,
} from "@mui/material";
import { socket } from "../modules/socket";
import axios from "axios";
import { Navigate } from "react-router-dom";

function Create({ user, setUser }) {
  const [roomID, setRoomID] = useState("");
  const [status, setStatus] = useState("SEND");
  const [submitted, setSubmitted] = useState(false);
  const timerRef = useRef(null);

  const [state, setState] = useState({
    username: "",
    lives: "6",
    numRounds: "3",
    rotation: "king",
    time: "30",
  });

  const [form, setForm] = useState({
    name: "",
    email: "",
    feedback: "",
  });

  // Handle room link from server
  const handleLink = useCallback(
    ({ gameState, roomID }) => {
      setUser(gameState.players[0]);
      setRoomID(roomID);
    },
    [setUser]
  );

  useEffect(() => {
    socket.on("link", handleLink);
    return () => socket.off("link", handleLink);
  }, [handleLink]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!state.username.trim()) return;
    setSubmitted(true);
    socket.emit("create", state);
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    if (timerRef.current) clearTimeout(timerRef.current);

    setStatus("Sent!");
    try {
      await axios.post(`${process.env.REACT_APP_SERVER}/feedback/`, { data: form });
      setForm({ name: "", email: "", feedback: "" });
    } catch (err) {
      console.error("Feedback submission failed", err);
    }

    timerRef.current = setTimeout(() => setStatus("SEND"), 5000);
  };

  const validateUsername = (name) => {
    if (!/^[^\s]+(\s+[^\s]+)*$/.test(name)) return "No leading/trailing spaces";
    if (name.length > 20) return "Max 20 characters";
    return "";
  };

  if (user && roomID) return <Navigate to={`/${roomID}`} />;

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: { xs: "column", md: "row" },
        gap: 4,
        p: 4,
        justifyContent: "center",
        alignItems: "flex-start",
      }}
    >
      {/* Info & Feedback Card */}
      <Card sx={{ flex: 1, minWidth: 300, boxShadow: 3 }}>
        <CardContent>
          <Typography variant="h5" gutterBottom>
            About
          </Typography>
          <Typography variant="body1" paragraph>
            Hangmanonline.io is a free online platform to play Hangman with your friends.
          </Typography>

          <Divider sx={{ my: 2 }} />

          <Typography variant="h5" gutterBottom>
            How to Play
          </Typography>
          <Typography variant="body1" paragraph>
            When it's your turn to be the "hanger", choose a word and category, then let your friends guess!
          </Typography>

          <Divider sx={{ my: 2 }} />

          <Typography variant="h5" gutterBottom>
            Feedback
          </Typography>
          <form onSubmit={handleFeedbackSubmit} style={{ marginTop: 16 }}>
            <TextField
              label="Name"
              value={form.name}
              onChange={(e) => setForm({ ...form, name: e.target.value })}
              fullWidth
              margin="normal"
              disabled={status === "Sent!"}
              required
            />
            <TextField
              label="Email"
              type="email"
              value={form.email}
              onChange={(e) => setForm({ ...form, email: e.target.value })}
              fullWidth
              margin="normal"
              disabled={status === "Sent!"}
              required
            />
            <TextField
              label="Feedback"
              multiline
              rows={4}
              value={form.feedback}
              onChange={(e) => setForm({ ...form, feedback: e.target.value })}
              fullWidth
              margin="normal"
              disabled={status === "Sent!"}
              required
            />
            <Button type="submit" variant="contained" sx={{ mt: 2 }}>
              {status}
            </Button>
          </form>
        </CardContent>
      </Card>

      {/* Game Creation Card */}
      <Card sx={{ flex: 1, minWidth: 300, boxShadow: 3 }}>
        <CardContent>
          <Typography variant="h4" gutterBottom>
            Play
          </Typography>
          <form onSubmit={handleSubmit}>
            <FormGroup>
              <TextField
                label="Username"
                variant="filled"
                value={state.username}
                onChange={(e) => setState({ ...state, username: e.target.value })}
                error={!!validateUsername(state.username)}
                helperText={validateUsername(state.username)}
                disabled={submitted}
                required
                margin="normal"
              />
              <TextField
                label="Lives"
                type="number"
                variant="filled"
                value={state.lives}
                onChange={(e) => setState({ ...state, lives: e.target.value })}
                inputProps={{ min: 6, max: 10 }}
                disabled={submitted}
                required
                margin="normal"
              />
              <TextField
                label="Rounds"
                type="number"
                variant="filled"
                value={state.numRounds}
                onChange={(e) => setState({ ...state, numRounds: e.target.value })}
                inputProps={{ min: 1 }}
                disabled={submitted}
                required
                margin="normal"
              />
              <FormControl variant="filled" margin="normal">
                <InputLabel>Rotation</InputLabel>
                <Select
                  value={state.rotation}
                  onChange={(e) => setState({ ...state, rotation: e.target.value })}
                  disabled={submitted}
                  required
                >
                  <MenuItem value="king">King of the Hill</MenuItem>
                  <MenuItem value="robin">Round Robin</MenuItem>
                </Select>
              </FormControl>
              <FormControl variant="filled" margin="normal">
                <InputLabel>Guess Time (sec)</InputLabel>
                <Select
                  value={state.time}
                  onChange={(e) => setState({ ...state, time: e.target.value })}
                  disabled={submitted}
                  required
                >
                  {["10", "20", "30", "40", "50", "60", "70", "80", "90", "inf"].map(
                    (t) => (
                      <MenuItem key={t} value={t}>
                        {t}
                      </MenuItem>
                    )
                  )}
                </Select>
              </FormControl>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                sx={{ mt: 2 }}
                disabled={submitted || !!validateUsername(state.username)}
              >
                Create Game
              </Button>
            </FormGroup>
          </form>
    
        </CardContent>
      </Card>
    </Box>
  );
}

export default Create;
