import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import HangmanCanvas from "./HangmanCanvas";
import WordDisplay from "./WordDisplay";
import PlayerList from "./PlayerList";
import Chat from "./Chat";
import Leaderboard from "./Leaderboard";

const socket = io("http://localhost:5000"); // your backend URL

export default function GameRoom({ playerName, roomId }) {
  const [roomData, setRoomData] = useState(null);
  const [gameState, setGameState] = useState(null);
  const [letter, setLetter] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    socket.emit("joinRoom", { playerName, roomId });

    socket.on("roomData", (data) => setRoomData(data));
    socket.on("gameState", (state) => setGameState(state));

    return () => socket.disconnect();
  }, [playerName, roomId]);

  const guessLetter = () => {
    if (!letter) return;
    socket.emit("guessLetter", letter);
    setLetter("");
  };

  const sendMessage = () => {
    if (!message) return;
    socket.emit("sendMessage", message);
    setMessage("");
  };

  if (!gameState) return <div>Loading...</div>;

  return (
    <div className="flex flex-col md:flex-row w-full h-full gap-4 p-4">
      {/* Left side: game */}
      <div className="flex-1 flex flex-col items-center gap-4">
        <HangmanCanvas strikes={gameState.globalStrikes} maxStrikes={gameState.maxStrikes} />
        <WordDisplay word={gameState.secretWordRevealed} />
        {gameState.currentTurnPlayerId === roomData.playerId && (
          <div className="flex gap-2 mt-2">
            <input
              type="text"
              value={letter}
              onChange={(e) => setLetter(e.target.value)}
              maxLength={1}
              className="border p-2 rounded text-center w-12"
            />
            <button
              onClick={guessLetter}
              className="bg-green-500 text-white p-2 rounded hover:bg-green-600 transition"
            >
              Guess
            </button>
          </div>
        )}
      </div>

      {/* Right side: players, chat, leaderboard */}
      <div className="w-96 flex flex-col gap-4">
        <PlayerList players={gameState.players} currentPlayerId={roomData.playerId} />
        <Leaderboard players={gameState.players} />
        <Chat
          messages={gameState.messages}
          message={message}
          setMessage={setMessage}
          sendMessage={sendMessage}
        />
      </div>
    </div>
  );
}
