import React from 'react';

function WordDisplay({ revealedWord, guessedLetters }) {
  return (
    <div className="space-y-6">
      <div className="flex gap-3 justify-center flex-wrap">
        {revealedWord.map((letter, index) => (
          <div 
            key={index}
            className="w-12 h-16 flex items-center justify-center border-b-4 border-gray-800 text-3xl font-bold text-gray-800"
          >
            {letter}
          </div>
        ))}
      </div>
      
      <div className="bg-gray-50 rounded-xl p-4">
        <h4 className="text-sm font-bold text-gray-600 mb-3 text-center">Guessed Letters</h4>
        <div className="flex gap-2 justify-center flex-wrap">
          {guessedLetters.length > 0 ? (
            guessedLetters.map((letter, index) => (
              <span 
                key={index}
                className="px-3 py-1 bg-gray-200 text-gray-700 rounded-lg font-semibold"
              >
                {letter}
              </span>
            ))
          ) : (
            <span className="text-gray-400 italic text-sm">None yet</span>
          )}
        </div>
      </div>
    </div>
  );
}

export default WordDisplay;
