import { io } from "socket.io-client";

// Ensure the backend URL is used; fallback to localhost:5000
const SOCKET_URL = process.env.REACT_APP_SERVER || "http://localhost:5000";

const socket = io(SOCKET_URL, {
  transports: ["websocket"],
});

export { socket };
