import axios from "axios";

const BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

export const createRoom = async ({ hostName, totalRounds, turnDuration, maxStrikes }) => {
  const res = await axios.post(`${BASE_URL}/create-room`, { hostName, totalRounds, turnDuration, maxStrikes });
  return res.data;
};
