import React from "react";

export default function HangmanCanvas({ strikes, maxStrikes }) {
  const parts = [
    "ü™ù Head",
    "ü¶¥ Body",
    "ü¶¥ Left Arm",
    "ü¶¥ Right Arm",
    "ü¶¥ Left Leg",
    "ü¶¥ Right Leg",
  ];
  const visibleParts = parts.slice(0, strikes);

  return (
    <div className="bg-white p-4 rounded-2xl shadow-md w-64 h-64 flex flex-col items-center justify-center">
      <div className="text-xl font-mono">{visibleParts.join(" | ")}</div>
      <div className="mt-2 text-gray-500">
        {strikes}/{maxStrikes} strikes
      </div>
    </div>
  );
}
