import React, { useState } from "react";

function HomePage({ onCreateRoom, onJoinRoom }) {
  const [roomCode, setRoomCode] = useState("");
  const [showHowToPlay, setShowHowToPlay] = useState(false);
  const [showFeedback, setShowFeedback] = useState(false);
  const [status, setStatus] = useState("");

  const handleJoin = () => {
    if (!roomCode.trim()) {
      setStatus("Please enter a valid room code.");
      return;
    }
    setStatus("Joining room...");
    onJoinRoom(roomCode);
  };

  const handleCreate = () => {
    setStatus("Creating room...");
    onCreateRoom();
  };

  const handleFeedbackSubmit = (e) => {
    e.preventDefault();
    const value = e.target.elements.feedback.value.trim();

    if (!value) {
      setStatus("Feedback cannot be empty.");
      return;
    }

    setStatus("Thank you for your feedback!");
    setShowFeedback(false);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-900 text-white">
      <h1 className="text-4xl font-extrabold mb-8">ðŸŽ® Multiplayer Hangman</h1>

      {/* STATUS */}
      {status && (
        <p className="mb-4 text-yellow-400 font-medium animate-pulse">
          {status}
        </p>
      )}

      <div className="bg-gray-800 p-8 rounded-2xl shadow-2xl w-80 flex flex-col gap-4">
        <button
          onClick={handleCreate}
          className="w-full py-3 bg-blue-600 hover:bg-blue-700 rounded-xl font-semibold transition"
        >
          Create Room
        </button>

        <div className="flex flex-col gap-3">
          <input
            type="text"
            placeholder="Enter Room Code"
            className="w-full p-3 rounded-xl bg-gray-700 text-white focus:ring-2 focus:ring-blue-500 outline-none"
            value={roomCode}
            onChange={(e) => setRoomCode(e.target.value)}
          />
          <button
            onClick={handleJoin}
            className="w-full py-3 bg-green-600 hover:bg-green-700 rounded-xl font-semibold transition"
          >
            Join Room
          </button>
        </div>

        <button
          onClick={() => setShowHowToPlay(true)}
          className="w-full py-2 bg-gray-700 hover:bg-gray-600 rounded-xl transition"
        >
          How to Play
        </button>

        <button
          onClick={() => setShowFeedback(true)}
          className="w-full py-2 bg-gray-700 hover:bg-gray-600 rounded-xl transition"
        >
          Feedback
        </button>
      </div>

      {/* HOW TO PLAY POPUP */}
      {showHowToPlay && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center">
          <div className="bg-gray-800 p-6 rounded-2xl w-80 text-center">
            <h2 className="text-2xl font-bold mb-4">How to Play</h2>
            <p>â€¢ One player sets a word.</p>
            <p>â€¢ Others guess letters.</p>
            <p>â€¢ Wrong guesses draw the hangman.</p>
            <p>â€¢ Guess the word before it's too late!</p>

            <button
              onClick={() => setShowHowToPlay(false)}
              className="mt-4 w-full py-2 bg-red-600 hover:bg-red-700 rounded-xl transition"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* FEEDBACK POPUP */}
      {showFeedback && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center">
          <div className="bg-gray-800 p-6 rounded-2xl w-80 text-center">
            <h2 className="text-2xl font-bold mb-4">Feedback</h2>

            <form onSubmit={handleFeedbackSubmit} className="flex flex-col gap-3">
              <textarea
                name="feedback"
                placeholder="Write your feedback..."
                className="w-full h-28 p-3 bg-gray-700 text-white rounded-xl focus:ring-2 focus:ring-blue-500 outline-none"
              ></textarea>

              <button
                type="submit"
                className="w-full py-2 bg-blue-600 hover:bg-blue-700 rounded-xl transition"
              >
                Submit
              </button>
            </form>

            <button
              onClick={() => setShowFeedback(false)}
              className="mt-3 w-full py-2 bg-red-600 hover:bg-red-700 rounded-xl transition"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default HomePage;
