import React, { useEffect, useRef } from "react";

function HangmanCanvas({ strikes, maxStrikes }) {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.strokeStyle = "#333";
    ctx.lineWidth = 3;
    ctx.lineCap = "round";

    // ------------------------
    // DRAW GALLOWS
    // ------------------------
    ctx.beginPath();
    ctx.moveTo(50, 250);
    ctx.lineTo(150, 250); // base
    ctx.stroke();

    ctx.beginPath();
    ctx.moveTo(100, 250);
    ctx.lineTo(100, 50); // vertical pole
    ctx.stroke();

    ctx.beginPath();
    ctx.moveTo(100, 50);
    ctx.lineTo(200, 50); // top beam
    ctx.stroke();

    ctx.beginPath();
    ctx.moveTo(200, 50);
    ctx.lineTo(200, 80); // rope drop
    ctx.stroke();

    // ------------------------
    // DYNAMIC HANGMAN STEPS
    // ------------------------

    // Total possible steps for Hangman body = 10
    // We scale strikes to this range
    const steps = Math.min(strikes, maxStrikes);
    const ratio = steps / maxStrikes; // 0 → 1 scale
    const scaledStep = Math.round(ratio * 10); // up to 10 steps

    // Step helper
    const step = (n) => scaledStep >= n;

    // 1. Head
    if (step(1)) {
      ctx.beginPath();
      ctx.arc(200, 100, 20, 0, Math.PI * 2);
      ctx.stroke();
    }

    // 2. Body
    if (step(2)) {
      ctx.beginPath();
      ctx.moveTo(200, 120);
      ctx.lineTo(200, 180);
      ctx.stroke();
    }

    // 3. Left arm
    if (step(3)) {
      ctx.beginPath();
      ctx.moveTo(200, 140);
      ctx.lineTo(170, 160);
      ctx.stroke();
    }

    // 4. Right arm
    if (step(4)) {
      ctx.beginPath();
      ctx.moveTo(200, 140);
      ctx.lineTo(230, 160);
      ctx.stroke();
    }

    // 5. Left leg
    if (step(5)) {
      ctx.beginPath();
      ctx.moveTo(200, 180);
      ctx.lineTo(180, 220);
      ctx.stroke();
    }

    // 6. Right leg
    if (step(6)) {
      ctx.beginPath();
      ctx.moveTo(200, 180);
      ctx.lineTo(220, 220);
      ctx.stroke();
    }

    // EXTRA STEPS FOR 7–10 STRIKES

    // 7. Tight rope / noose detail
    if (step(7)) {
      ctx.beginPath();
      ctx.moveTo(200, 80);
      ctx.lineTo(200, 90);
      ctx.stroke();
    }

    // 8. Torso curve (detail)
    if (step(8)) {
      ctx.beginPath();
      ctx.moveTo(200, 150);
      ctx.lineTo(190, 160);
      ctx.stroke();
    }

    // 9. Eyes (X X)
    if (step(9)) {
      ctx.beginPath();
      ctx.moveTo(195, 95);
      ctx.lineTo(198, 98);
      ctx.moveTo(198, 95);
      ctx.lineTo(195, 98);

      ctx.moveTo(202, 95);
      ctx.lineTo(205, 98);
      ctx.moveTo(205, 95);
      ctx.lineTo(202, 98);

      ctx.stroke();
    }

    // 10. Mouth (sad)
    if (step(10)) {
      ctx.beginPath();
      ctx.moveTo(195, 110);
      ctx.quadraticCurveTo(200, 115, 205, 110);
      ctx.stroke();
    }

  }, [strikes, maxStrikes]);

  return (
    <div className="hangman-canvas">
      <canvas ref={canvasRef} width={300} height={300} />
    </div>
  );
}

export default HangmanCanvas;
