import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import Game from "./Game";
import Wait from "./Wait";
import Chat from "./Chat";
import NewWord from "./NewWord";
import axios from "axios";
import "../css/Room.scss";
import { socket } from "../modules/socket";
import { Box, Typography, Paper, List, ListItem, ListItemText } from "@mui/material";

function Room({ username, mute }) {
  const [gameState, setGameState] = useState(null);
  const [user, setUser] = useState(username);
  const { roomID } = useParams();
  const [err, setErr] = useState(false);

  const handleLeave = useCallback((newState) => {
    if (!newState || !Array.isArray(newState.players) || newState.players.length === 0) {
      setErr(true);
    } else {
      setGameState({ ...newState });
    }
  }, []);

  useEffect(() => {
    const getGameState = async () => {
      try {
        const backendURL = process.env.REACT_APP_SERVER || "http://localhost:5000";
        const res = await axios.get(`${backendURL}/?roomID=${roomID}`);
        if (res.status === 200 && res.data && res.data.players) {
          setGameState(res.data);
        } else {
          setErr(true);
        }
      } catch {
        setErr(true);
      }
    };

    socket.on("leave", handleLeave);
    getGameState();
    return () => socket.off("leave", handleLeave);
  }, [roomID, handleLeave]);

  useEffect(() => {
    const cleanup = () => {
      socket.emit("leave", { user, roomID });
    };
    window.addEventListener("unload", cleanup);
    return () => window.removeEventListener("unload", cleanup);
  }, [user, roomID]);

  const renderContent = () => {
    if (err) return <Typography color="error">Room does not exist</Typography>;
    if (!gameState) return <Typography>Loading...</Typography>;

    if (user === "" || !gameState.gameStart) {
      return (
        <Wait
          user={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          setUser={setUser}
          mute={mute}
        />
      );
    }

    if (gameState.gameStart && gameState.category !== "") {
      return (
        <Game
          username={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          mute={mute}
        />
      );
    }

    if (gameState.category === "") {
      return (
        <NewWord
          gameState={gameState}
          setGameState={setGameState}
          user={user}
          roomID={roomID}
          mute={mute}
        />
      );
    }

    return <Typography>Loading...</Typography>;
  };

  return (
    <Box sx={{ display: "flex", height: "95vh", width: "96vw", px: "8px", boxSizing: "border-box" }}>
      {/* Left Panel: Active players, hangman, mode */}
      <Paper
        elevation={3}
        sx={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          p: 2,
          borderRadius: 0,
          overflowY: "auto",
          justifyContent: "flex-start",
        }}
      >
        {/* Room Info */}
        <Typography variant="h6" fontWeight="bold" textAlign="center" sx={{ mb: 2 }}>
          ðŸŽ® Room Code: {roomID.toUpperCase()}
        </Typography>

        {/* Active Players */}
        <Typography variant="subtitle1" fontWeight="bold" sx={{ mb: 1 }}>
          Players
        </Typography>
        <List dense>
          {gameState?.players?.map((p, idx) => (
            <ListItem key={p}>
              <ListItemText primary={`${idx + 1}. ${p}`} />
            </ListItem>
          ))}
        </List>

        {/* Hangman placeholder */}
        <Box sx={{ mt: 3, textAlign: "center" }}>
          <Typography variant="subtitle1" fontWeight="bold">
            Hangman
          </Typography>
          {/* Add your hangman component or image here */}
        </Box>

        {/* Mode placeholder */}
        <Box sx={{ mt: 2, textAlign: "center" }}>
          <Typography variant="subtitle1" fontWeight="bold">
            Mode: {gameState?.mode || "Classic"}
          </Typography>
        </Box>
      </Paper>

      {/* Center Panel: Game info (round, word, category) at top + main content */}
      <Box
        sx={{
          flex: 2,
          display: "flex",
          flexDirection: "column",
          overflow: "hidden",
          p: 2,
        }}
      >
        {/* Top info */}
        <Box sx={{ textAlign: "center", mb: 3 }}>
          <Typography variant="h6">
            Round: {gameState?.round || "-"}
          </Typography>
          <Typography variant="h6">
            Word: {gameState?.currentWord || "-"}
          </Typography>
          <Typography variant="h6">
            Category: {gameState?.category || "-"}
          </Typography>
        </Box>

        {/* Main content */}
        <Box sx={{ flex: 1, display: "flex", justifyContent: "center", alignItems: "flex-start" }}>
          {renderContent()}
        </Box>
      </Box>

      {/* Right Panel: Chat */}
      <Paper
        elevation={3}
        sx={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          p: 2,
          borderRadius: 0,
          overflowY: "auto",
        }}
      >
        <Typography variant="h6" fontWeight="bold" textAlign="center" sx={{ mb: 2 }}>
          ðŸ’¬ Chat
        </Typography>
        {user && <Chat user={user} roomID={roomID} />}
      </Paper>
    </Box>
  );
}

export default Room;
