import { useState } from "react";

export default function Chat({ socket, roomId, playerId }) {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  if (!socket) return null;

  socket.off("new-message").on("new-message", (msg) => setMessages(prev => [...prev, msg]));

  const send = () => {
    if (!message) return;
    socket.emit("send-message", { roomId, playerId, message });
    setMessage("");
  };

  return (
    <div>
      <div style={{ maxHeight: "200px", overflowY: "scroll" }}>
        {messages.map(m => <p key={m.id}><strong>{m.playerName}:</strong> {m.message}</p>)}
      </div>
      <input value={message} onChange={e => setMessage(e.target.value)} />
      <button onClick={send}>Send</button>
    </div>
  );
}
