import React, { useState, useEffect, useRef, useCallback } from "react";
import { socket } from "../modules/socket";
import axios from "axios";
import { Navigate } from "react-router-dom";

/**
 * Create.jsx
 * Neon gaming UI (pink + blue) — preserves exact functionality from your original component.
 *
 * Props:
 * - user: current user object (from parent)
 * - setUser: setter to store the created player
 *
 * Behaviors preserved:
 * - socket.emit("create", state)
 * - socket.on("link", ({ gameState, roomID }) => { setUser(...); setRoomID(...) })
 * - Redirect to /:roomID when user && roomID
 * - username validation
 * - feedback POST to process.env.REACT_APP_SERVER/feedback/
 */

export default function Create({ user, setUser }) {
  const [roomID, setRoomID] = useState("");
  const [submitted, setSubmitted] = useState(false);
  const timerRef = useRef(null);

  const [state, setState] = useState({
    username: "",
    lives: "6",
    numRounds: "3",
    rotation: "king",
    time: "30",
  });

  const [form, setForm] = useState({
    name: "",
    email: "",
    feedback: "",
  });

  const [status, setStatus] = useState("SEND");

  const validateUsername = (name) => {
    if (!/^[^\s]+(\s+[^\s]+)*$/.test(name)) return "No leading/trailing spaces";
    if (name.length > 20) return "Max 20 characters";
    return "";
  };

  // Keep handle stable and include setUser in deps (safe — setUser is a prop)
  const handleLink = useCallback(
    ({ gameState, roomID }) => {
      // same behaviour: first player becomes local user
      setUser(gameState.players[0]);
      setRoomID(roomID);
    },
    [setUser]
  );

  useEffect(() => {
    socket.on("link", handleLink);
    return () => socket.off("link", handleLink);
  }, [handleLink]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!state.username.trim()) return;
    setSubmitted(true);
    socket.emit("create", state);
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    if (timerRef.current) clearTimeout(timerRef.current);

    setStatus("Sent!");
    try {
      await axios.post(`${process.env.REACT_APP_SERVER}/feedback/`, { data: form });
      setForm({ name: "", email: "", feedback: "" });
    } catch (err) {
      console.error("Feedback submission failed", err);
    }
    timerRef.current = setTimeout(() => setStatus("SEND"), 4000);
  };

  // Redirect behavior preserved exactly
  if (user && roomID) return <Navigate to={`/${roomID}`} />;

  /* ---------------------------------------
     Neon theme variables (used via inline style for fallback)
     Primary neon: pink (#ec4899), accent blue (#3b82f6)
     --------------------------------------- */

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#07010a] via-[#07041a] to-[#05030a] text-white font-sans">
      {/* Top neon bar */}
      <header className="sticky top-0 z-50 w-full border-b border-border-dark/30 bg-black/30 backdrop-blur-md">
        <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
          <div className="flex items-center gap-4">
            <div
              className="w-11 h-11 rounded-xl flex items-center justify-center shadow-[0_6px_20px_rgba(236,72,153,0.14)]"
              style={{
                background:
                  "linear-gradient(135deg, rgba(236,72,153,0.14), rgba(59,130,246,0.06))",
                border: "1px solid rgba(236,72,153,0.12)",
              }}
            >
              <svg width="22" height="22" viewBox="0 0 24 24" fill="none">
                <path d="M12 2v7" stroke="#ec4899" strokeWidth="1.6" strokeLinecap="round" />
                <circle cx="12" cy="15" r="6" stroke="#3b82f6" strokeWidth="1.4" />
              </svg>
            </div>
            <h1 className="text-lg font-extrabold tracking-tight">Hangman — Neon Arena</h1>
          </div>

          <nav className="hidden md:flex items-center gap-6 text-sm text-gray-300">
            <a className="hover:text-white transition" href="#about">About</a>
            <a className="hover:text-white transition" href="#how-to-play">How to Play</a>
            <a className="hover:text-white transition" href="#feedback">Feedback</a>
            <a
              href="#create-game"
              className="ml-3 px-4 py-2 rounded-full font-semibold text-[#0b1020] bg-gradient-to-r from-[#ec4899] to-[#3b82f6] shadow-neon hover:scale-[1.02] transition-transform"
            >
              Create Game
            </a>
          </nav>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-6 py-12 grid grid-cols-1 lg:grid-cols-2 gap-10">
        {/* LEFT - marketing, accordions, feedback */}
        <div className="space-y-8">
          <section className="space-y-4">
            <h2 className="text-4xl md:text-5xl font-extrabold leading-tight">
              Challenge Your Friends — <span className="text-[#ec4899]">Win</span> the Hangman Arena
            </h2>
            <p className="text-gray-300 max-w-xl">
              Create a private match, share the link, and battle friends in fast-paced word-guessing rounds.
              Neon glow UI, responsive multiplayer, and seamless gameplay.
            </p>
          </section>

          {/* Accordions (About / How to play / Feedback preview) */}
          <div className="space-y-4">
            <details className="group bg-[#08101a]/60 border border-[#2a2540]/40 rounded-2xl p-4 backdrop-blur-sm">
              <summary className="flex justify-between items-center cursor-pointer">
                <div>
                  <h3 className="text-lg font-bold">About</h3>
                  <div className="text-sm text-gray-400">What is Hangman Neon Arena?</div>
                </div>
                <div className="text-2xl text-[#ec4899] group-open:rotate-180 transition-transform">▾</div>
              </summary>
              <div className="mt-4 text-gray-300">
                Hangmanonline.io — now neonified. Build rooms, set rules, and play rounds with friends. Fast, minimal latency, and mobile-friendly.
              </div>
            </details>

            <details className="group bg-[#08101a]/60 border border-[#2a2540]/40 rounded-2xl p-4 backdrop-blur-sm">
              <summary className="flex justify-between items-center cursor-pointer">
                <div>
                  <h3 className="text-lg font-bold">How to Play</h3>
                  <div className="text-sm text-gray-400">3 steps to get started</div>
                </div>
                <div className="text-2xl text-[#3b82f6] group-open:rotate-180 transition-transform">▾</div>
              </summary>
              <ol className="mt-4 pl-5 list-decimal text-gray-300 space-y-2">
                <li>Create a game and copy the link.</li>
                <li>Invite friends to join the match.</li>
                <li>Guess letters — don't get hanged!</li>
              </ol>
            </details>

            {/* Quick feedback mini-form (mirrors the right-side detailed feedback) */}
            <details id="feedback" className="group bg-[#08101a]/60 border border-[#2a2540]/40 rounded-2xl p-4 backdrop-blur-sm">
              <summary className="flex justify-between items-center cursor-pointer">
                <div>
                  <h3 className="text-lg font-bold">Feedback</h3>
                  <div className="text-sm text-gray-400">Tell us what you think</div>
                </div>
                <div className="text-2xl text-[#ec4899] group-open:rotate-180 transition-transform">▾</div>
              </summary>

              <form onSubmit={handleFeedbackSubmit} className="mt-4 grid grid-cols-1 gap-3">
                <input
                  placeholder="Name"
                  value={form.name}
                  onChange={(e) => setForm({ ...form, name: e.target.value })}
                  disabled={status === "Sent!"}
                  className="w-full rounded-lg p-2 bg-[#06060c] border border-[#2b2a3a] placeholder:text-gray-500"
                  required
                />
                <input
                  placeholder="Email"
                  type="email"
                  value={form.email}
                  onChange={(e) => setForm({ ...form, email: e.target.value })}
                  disabled={status === "Sent!"}
                  className="w-full rounded-lg p-2 bg-[#06060c] border border-[#2b2a3a] placeholder:text-gray-500"
                  required
                />
                <textarea
                  rows={3}
                  placeholder="Feedback"
                  value={form.feedback}
                  onChange={(e) => setForm({ ...form, feedback: e.target.value })}
                  disabled={status === "Sent!"}
                  className="w-full rounded-lg p-2 bg-[#06060c] border border-[#2b2a3a] placeholder:text-gray-500"
                  required
                />

                <div className="flex gap-3">
                  <button
                    type="submit"
                    className="flex-1 rounded-full py-2 px-4 font-semibold bg-gradient-to-r from-[#ec4899] to-[#3b82f6] text-[#070812] shadow-[0_10px_30px_rgba(236,72,153,0.18)] hover:scale-[1.02] transition"
                    aria-label="Send feedback"
                  >
                    {status}
                  </button>
                  <button
                    type="button"
                    onClick={() => setForm({ name: "", email: "", feedback: "" })}
                    className="rounded-full py-2 px-4 border border-[#2b2a3a] text-gray-300 hover:bg-white/3 transition"
                  >
                    Clear
                  </button>
                </div>
              </form>
            </details>
          </div>
        </div>

        {/* RIGHT - Create Game Card */}
        <div id="create-game" className="relative">
          <div
            className="rounded-3xl p-6 lg:p-8 border"
            style={{
              background:
                "linear-gradient(180deg, rgba(8,10,14,0.75), rgba(4,6,10,0.5))",
              borderColor: "rgba(59,130,246,0.08)",
              boxShadow:
                "0 8px 40px rgba(59,130,246,0.06), inset 0 1px 0 rgba(236,72,153,0.03)",
            }}
          >
            <div className="flex items-center justify-between mb-4">
              <div>
                <h3 className="text-2xl font-extrabold">Create a New Game</h3>
                <div className="text-sm text-gray-400">Fast matches · Shareable links</div>
              </div>
              <div className="text-xs px-3 py-1 rounded-full bg-white/5 text-[#ec4899] font-semibold">
                Neon • v1
              </div>
            </div>

            <form onSubmit={handleSubmit} className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              {/* USERNAME */}
              <div className="sm:col-span-2">
                <label className="block text-xs text-gray-300 mb-2">Username</label>
                <div className="relative">
                  <input
                    className="w-full rounded-lg p-3 bg-[#02030a] border border-[#222134] placeholder:text-gray-500 focus:outline-none focus:ring-2 focus:ring-[#ec4899]/25"
                    placeholder="Enter your username"
                    value={state.username}
                    onChange={(e) => setState({ ...state, username: e.target.value })}
                    disabled={submitted}
                    required
                  />
                  {/* neon hint */}
                  <div className="absolute right-3 top-3 text-sm text-gray-400">max 20</div>
                </div>
                {validateUsername(state.username) && (
                  <p className="text-rose-400 mt-1 text-sm">{validateUsername(state.username)}</p>
                )}
              </div>

              {/* LIVES */}
              <div>
                <label className="block text-xs text-gray-300 mb-2">Lives</label>
                <input
                  type="number"
                  min="6"
                  max="10"
                  className="w-full rounded-lg p-3 bg-[#02030a] border border-[#222134] focus:outline-none"
                  value={state.lives}
                  onChange={(e) => setState({ ...state, lives: e.target.value })}
                  disabled={submitted}
                  required
                />
              </div>

              {/* ROUNDS */}
              <div>
                <label className="block text-xs text-gray-300 mb-2">Rounds</label>
                <input
                  type="number"
                  min="1"
                  className="w-full rounded-lg p-3 bg-[#02030a] border border-[#222134] focus:outline-none"
                  value={state.numRounds}
                  onChange={(e) => setState({ ...state, numRounds: e.target.value })}
                  disabled={submitted}
                  required
                />
              </div>

              {/* ROTATION */}
              <div>
                <label className="block text-xs text-gray-300 mb-2">Rotation</label>
                <select
                  value={state.rotation}
                  onChange={(e) => setState({ ...state, rotation: e.target.value })}
                  disabled={submitted}
                  className="w-full rounded-lg p-3 bg-[#02030a] border border-[#222134] focus:outline-none"
                  required
                >
                  <option value="king">King of the Hill</option>
                  <option value="robin">Round Robin</option>
                </select>
              </div>

              {/* TIME */}
              <div>
                <label className="block text-xs text-gray-300 mb-2">Guess Time (sec)</label>
                <select
                  value={state.time}
                  onChange={(e) => setState({ ...state, time: e.target.value })}
                  disabled={submitted}
                  className="w-full rounded-lg p-3 bg-[#02030a] border border-[#222134] focus:outline-none"
                  required
                >
                  {["10","20","30","40","50","60","70","80","90","inf"].map((t) => (
                    <option key={t} value={t}>{t}</option>
                  ))}
                </select>
              </div>

              {/* CREATE BUTTON */}
              <div className="sm:col-span-2 mt-1">
                <button
                  type="submit"
                  disabled={submitted || !!validateUsername(state.username)}
                  className={`w-full rounded-full py-3 font-bold text-[#071022] transform transition-all ${
                    submitted || !!validateUsername(state.username)
                      ? "bg-gray-600 cursor-not-allowed opacity-60"
                      : "bg-gradient-to-r from-[#ec4899] to-[#3b82f6] shadow-[0_12px_40px_rgba(59,130,246,0.12),0_6px_20px_rgba(236,72,153,0.12)] hover:scale-[1.02]"
                  }`}
                >
                  Create Game
                </button>
              </div>
            </form>

            <div className="mt-4 text-sm text-gray-400">
              Creators: <span className="text-gray-200">Vikhram Thirupathi</span>,{" "}
              <span className="text-gray-200">Srihari Srinivasan</span>,{" "}
              <span className="text-gray-200">Rimaz Khan</span>
            </div>
          </div>

          {/* small neon accent below card */}
          <div className="mt-6 flex items-center justify-center gap-3">
            <div className="w-28 h-[2px] rounded bg-gradient-to-r from-[#ec4899] to-transparent opacity-80" />
            <div className="text-xs text-gray-400">Neon Arena</div>
            <div className="w-28 h-[2px] rounded bg-gradient-to-l from-[#3b82f6] to-transparent opacity-80" />
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="mt-12 py-6 border-t border-t-[#0b0b12]">
        <div className="max-w-7xl mx-auto px-6 text-center text-sm text-gray-400">
          © {new Date().getFullYear()} Hangman Neon Arena — Built with ❤️ in React
        </div>
      </footer>

      {/* Tailwind custom shadow class - include inline style fallback if your tailwind config lacks it */}
      <style>{`
        .shadow-neon {
          box-shadow: 0 12px 30px rgba(236,72,153,0.12), inset 0 1px 0 rgba(255,255,255,0.02);
        }
      `}</style>
    </div>
  );
}
