import React, { useState, useEffect, useRef, useCallback } from "react";
import { socket } from "../modules/socket";
import axios from "axios";
import { Navigate } from "react-router-dom";

export default function Create({ user, setUser }) {
  const [roomID, setRoomID] = useState("");
  const [submitted, setSubmitted] = useState(false);
  const timerRef = useRef(null);

  const [state, setState] = useState({
    username: "",
    lives: "6",
    numRounds: "3",
    rotation: "king",
    time: "30",
  });

  const [form, setForm] = useState({
    name: "",
    email: "",
    feedback: "",
  });

  const [status, setStatus] = useState("SEND");

  const validateUsername = (name) => {
    if (!/^[^\s]+(\s+[^\s]+)*$/.test(name)) return "No leading/trailing spaces";
    if (name.length > 20) return "Max 20 characters";
    return "";
  };

  // Socket: handle link
  const handleLink = useCallback(
    ({ gameState, roomID }) => {
      setUser(gameState.players[0]);
      setRoomID(roomID);
    },
    [setUser]
  );

  useEffect(() => {
    socket.on("link", handleLink);
    return () => socket.off("link", handleLink);
  }, [handleLink]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!state.username.trim()) return;
    setSubmitted(true);
    socket.emit("create", state);
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    if (timerRef.current) clearTimeout(timerRef.current);

    setStatus("Sent!");
    try {
      await axios.post(`${process.env.REACT_APP_SERVER}/feedback/`, { data: form });
      setForm({ name: "", email: "", feedback: "" });
    } catch (err) {
      console.error("Feedback failed", err);
    }
    timerRef.current = setTimeout(() => setStatus("SEND"), 4000);
  };

  if (user && roomID) return <Navigate to={`/${roomID}`} />;

  return (
    <div className="bg-background-dark min-h-screen w-full text-text-primary font-display">
      <div className="max-w-7xl mx-auto p-6 sm:p-10 lg:p-20 grid grid-cols-1 lg:grid-cols-2 gap-10">

        {/* LEFT SIDE */}
        <div className="space-y-10">
          <section className="space-y-4">
            <h1 className="text-4xl font-black sm:text-5xl">Challenge Your Friends.</h1>
            <p className="text-text-secondary text-lg">
              Enter your username and game settings to create your own Hangman match.
            </p>
          </section>

          {/* About accordion */}
          <details className="bg-surface-dark rounded-lg border border-border-dark p-4">
            <summary className="cursor-pointer text-xl font-bold">About</summary>
            <p className="mt-3 text-text-secondary">
              Hangmanonline.io is a free online platform to play Hangman with your friends.
              Create a room, share the link, and enjoy!
            </p>
          </details>

          {/* How to play */}
          <details className="bg-surface-dark rounded-lg border border-border-dark p-4">
            <summary className="cursor-pointer text-xl font-bold">How to Play</summary>
            <ol className="mt-3 list-decimal pl-5 text-text-secondary space-y-2">
              <li>Create a game.</li>
              <li>Share the link with your friends.</li>
              <li>Guess letters and avoid getting hanged!</li>
            </ol>
          </details>

          {/* Feedback */}
          <details className="bg-surface-dark rounded-lg border border-border-dark p-4">
            <summary className="cursor-pointer text-xl font-bold">Feedback</summary>
            <form className="mt-4 space-y-4" onSubmit={handleFeedbackSubmit}>
              <div>
                <label className="text-sm">Name</label>
                <input
                  className="w-full bg-background-dark border border-border-dark rounded-md p-2 mt-1"
                  value={form.name}
                  onChange={(e) => setForm({ ...form, name: e.target.value })}
                  disabled={status === "Sent!"}
                  required
                />
              </div>
              <div>
                <label className="text-sm">Email</label>
                <input
                  type="email"
                  className="w-full bg-background-dark border border-border-dark rounded-md p-2 mt-1"
                  value={form.email}
                  onChange={(e) => setForm({ ...form, email: e.target.value })}
                  disabled={status === "Sent!"}
                  required
                />
              </div>
              <div>
                <label className="text-sm">Feedback</label>
                <textarea
                  rows="4"
                  className="w-full bg-background-dark border border-border-dark rounded-md p-3 mt-1"
                  value={form.feedback}
                  onChange={(e) => setForm({ ...form, feedback: e.target.value })}
                  disabled={status === "Sent!"}
                  required
                />
              </div>
              <button
                type="submit"
                className="bg-accent text-text-primary w-full rounded-full p-3 font-bold hover:scale-105 transition"
              >
                {status}
              </button>
            </form>
          </details>
        </div>

        {/* RIGHT SIDE â€” Create Game */}
        <div className="bg-surface-dark border border-border-dark rounded-lg p-6 lg:p-8 space-y-6 shadow-xl">
          <h3 className="text-2xl font-bold">Create a New Game</h3>

          <form className="grid grid-cols-1 sm:grid-cols-2 gap-6" onSubmit={handleSubmit}>
            {/* Username */}
            <div className="sm:col-span-2">
              <label className="block mb-2">Username</label>
              <input
                className="w-full bg-background-dark border border-border-dark rounded-md p-3"
                placeholder="Enter your username"
                value={state.username}
                onChange={(e) => setState({ ...state, username: e.target.value })}
                disabled={submitted}
                required
              />
              {validateUsername(state.username) && (
                <p className="text-red-500 text-sm mt-1">{validateUsername(state.username)}</p>
              )}
            </div>

            {/* Lives */}
            <div>
              <label className="block mb-2">Lives</label>
              <input
                type="number"
                className="w-full bg-background-dark border border-border-dark rounded-md p-3"
                value={state.lives}
                onChange={(e) => setState({ ...state, lives: e.target.value })}
                min="6"
                max="10"
                disabled={submitted}
                required
              />
            </div>

            {/* Rounds */}
            <div>
              <label className="block mb-2">Rounds</label>
              <input
                type="number"
                className="w-full bg-background-dark border border-border-dark rounded-md p-3"
                value={state.numRounds}
                onChange={(e) => setState({ ...state, numRounds: e.target.value })}
                min="1"
                disabled={submitted}
                required
              />
            </div>

            {/* Rotation */}
            <div>
              <label className="block mb-2">Rotation</label>
              <select
                className="w-full bg-background-dark border border-border-dark rounded-md p-3"
                value={state.rotation}
                onChange={(e) => setState({ ...state, rotation: e.target.value })}
                disabled={submitted}
                required
              >
                <option value="king">King of the Hill</option>
                <option value="robin">Round Robin</option>
              </select>
            </div>

            {/* Time */}
            <div>
              <label className="block mb-2">Guess Time (sec)</label>
              <select
                className="w-full bg-background-dark border border-border-dark rounded-md p-3"
                value={state.time}
                onChange={(e) => setState({ ...state, time: e.target.value })}
                disabled={submitted}
                required
              >
                {["10","20","30","40","50","60","70","80","90","inf"].map((t) => (
                  <option key={t} value={t}>{t}</option>
                ))}
              </select>
            </div>

            <button
              type="submit"
              disabled={submitted || !!validateUsername(state.username)}
              className="sm:col-span-2 bg-primary text-background-dark p-3 rounded-full font-bold hover:scale-105 transition"
            >
              Create Game
            </button>
          </form>

          <p className="text-sm text-text-secondary">
            Creators: Vikhram Thirupathi, Srihari Srinivasan, Rimaz Khan
          </p>
        </div>

      </div>
    </div>
  );
}
