import React from "react";

export default function WordDisplay({ revealed }) {
  if (!revealed) return null;
  return (
    <div style={{ fontSize: "2rem", letterSpacing: "0.5rem" }}>
      {revealed.split("").map((ch, i) => (
        <span key={i}>{ch}</span>
      ))}
    </div>
  );
}
