import React from 'react';

function GameOver({ leaderboard, onNextRound, onLeave, currentRound, totalRounds }) {
  const isGameComplete = currentRound >= totalRounds;

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500 flex items-center justify-center p-4">
      <div className="w-full max-w-2xl">
        <div className="bg-white rounded-3xl shadow-2xl p-10">
          <h1 className="text-4xl font-bold text-center mb-2">
            {isGameComplete ? 'üéâ Game Complete!' : 'üèÜ Round Results'}
          </h1>
          <p className="text-center text-gray-600 mb-8">
            {isGameComplete ? 'Final Standings' : `Round ${currentRound} of ${totalRounds}`}
          </p>
          
          <div className="mb-8">
            <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">Leaderboard</h2>
            <div className="space-y-3">
              {leaderboard.map((player, index) => (
                <div 
                  key={player.id}
                  className={`flex items-center justify-between p-5 rounded-xl transition-all ${
                    index === 0 
                      ? 'bg-gradient-to-r from-yellow-400 to-yellow-500 shadow-lg transform scale-105' 
                      : index === 1
                      ? 'bg-gradient-to-r from-gray-300 to-gray-400'
                      : index === 2
                      ? 'bg-gradient-to-r from-orange-300 to-orange-400'
                      : 'bg-gray-100'
                  }`}
                >
                  <div className="flex items-center gap-4">
                    <div className="text-2xl font-bold min-w-[40px]">
                      {index === 0 ? 'ü•á' : index === 1 ? 'ü•à' : index === 2 ? 'ü•â' : `#${index + 1}`}
                    </div>
                    <div className="font-bold text-lg text-gray-800">
                      {player.name}
                    </div>
                  </div>
                  <div className="text-2xl font-bold text-indigo-600">
                    {player.score} pts
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="flex gap-4 justify-center">
            {!isGameComplete && (
              <button 
                onClick={onNextRound}
                className="px-8 py-3 bg-indigo-600 hover:bg-indigo-700 text-white font-bold rounded-xl transition-all transform hover:scale-105 shadow-lg"
              >
                Next Round
              </button>
            )}
            <button 
              onClick={onLeave}
              className="px-8 py-3 bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold rounded-xl transition-all transform hover:scale-105"
            >
              Leave Game
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default GameOver;
