import React, { useState } from "react";
import { AppBar, Toolbar, Typography, Box, Button } from "@mui/material";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import ReactGA from "react-ga4";
import Create from "./Create";  
import Home from "./Home";
import Room from "./Room";
import "../css/Main.scss";


// Theme configuration
const theme = createTheme({
  typography: {
    fontFamily: ["Yeon Sung", "Sans Serif"].join(","),
    h3: {
      letterSpacing: "2px",
    },
  },
  palette: {
    primary: {
      main: "#2994ff",
    },
  },
});

// Initialize Google Analytics if key exists
const key = process.env.REACT_APP_ANALYTICS_KEY;
if (key) ReactGA.initialize(key);

const Main = () => {
  const [username, setUsername] = useState("");

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />

      {/* AppBar */}

<AppBar
  position="static"
  elevation={4} // subtle shadow
  sx={{
    backgroundColor: "#ffffff", // white background
    color: "#333", // text color
    borderBottom: "1px solid #e0e0e0", // soft divider
  }}
>
  <Toolbar sx={{ display: "flex", justifyContent: "space-between" }}>
    {/* Logo / Title */}
    <Typography
      variant="h6"
      component="div"
      sx={{
        fontWeight: "bold",
        letterSpacing: 1,
      }}
    >
      <a href="/" style={{ textDecoration: "none", color: "inherit" }}>
        HangmanOnline.io
      </a>
    </Typography>

    {/* Optional navigation buttons */}
    <Box sx={{ display: "flex", gap: 2 }}>
      <Button
        variant="outlined"
        color="primary"
        size="small"
        sx={{ borderRadius: 2, textTransform: "none" }}
        href="/create"
      >
        Create Room
      </Button>
      <Button
        variant="contained"
        color="primary"
        size="small"
        sx={{ borderRadius: 2, textTransform: "none" }}
        href="/join"
      >
        Join Room
      </Button>
    </Box>
  </Toolbar>
</AppBar>


      <div className="container">
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<Home setUsername={setUsername} />} />
            <Route path="/create" element={<Create setUser={setUsername} user={username} />} />
            <Route path="/:roomID" element={<Room username={username} />} />
          </Routes>
        </BrowserRouter>
      </div>
    </ThemeProvider>
  );
};

export default Main;
