import React from "react";

export default function Chat({ messages, message, setMessage, sendMessage }) {
  return (
    <div className="bg-white p-4 rounded-xl shadow-md flex flex-col gap-2 h-64 overflow-y-auto">
      <h2 className="font-bold">Chat</h2>
      <div className="flex-1 overflow-y-auto">
        {messages.map((m) => (
          <div key={m.id} className="text-sm">
            <strong>{m.playerName}:</strong> {m.message}
          </div>
        ))}
      </div>
      <div className="flex gap-2 mt-2">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="border p-2 rounded flex-1"
        />
        <button
          className="bg-purple-500 text-white p-2 rounded hover:bg-purple-600 transition"
          onClick={sendMessage}
        >
          Send
        </button>
      </div>
    </div>
  );
}
