import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import {
  Box,
  Button,
  Card,
  CardContent,
  TextField,
  Typography,
  Grid,
} from "@mui/material";
import { keyframes } from "@emotion/react";
import axios from "axios";

// Background gradient animation (darker)
const darkGradientAnimation = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

// Glassmorphism style
const glassCardStyle = {
  background: "rgba(255, 255, 255, 0.05)",
  backdropFilter: "blur(12px)",
  borderRadius: "20px",
  boxShadow: "0 8px 32px 0 rgba(0, 0, 0, 0.6)",
  border: "1px solid rgba(255, 255, 255, 0.1)",
  padding: "32px",
  transition: "all 0.3s ease",
};

const Home = () => {
  const navigate = useNavigate();
  const [roomIdInput, setRoomIdInput] = useState("");
  const [feedback, setFeedback] = useState({ name: "", email: "", message: "" });
  const [status, setStatus] = useState("SEND");
  const timerRef = useRef(null);

  const handleCreateRoom = () => navigate("/create");
  const handleJoinRoom = () => {
    if (roomIdInput.trim() !== "") navigate(`/${roomIdInput}`);
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    if (timerRef.current) clearTimeout(timerRef.current);
    setStatus("Sent!");
    try {
      await axios.post(`${process.env.REACT_APP_SERVER}/feedback/`, { data: feedback });
      setFeedback({ name: "", email: "", message: "" });
    } catch (err) {
      console.error(err);
      setStatus("Error!");
    }
    timerRef.current = setTimeout(() => setStatus("SEND"), 5000);
  };

  return (
    <Box
      sx={{
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "flex-start",
        gap: 6,
        px: 2,
        py: 6,
        background: "linear-gradient(-45deg, #1e1e2f, #2c2c3f, #3a3a4f, #1e1e2f)",
        backgroundSize: "400% 400%",
        animation: `${darkGradientAnimation} 20s ease infinite`,
      }}
    >
      {/* Header */}
      <Box sx={{ textAlign: "center" }}>
        <Typography
          variant="h2"
          sx={{
            fontFamily: "Comic Sans MS",
            color: "#fff",
            mb: 3,
            textShadow: "2px 2px 12px rgba(0,0,0,0.6)",
          }}
        >
          Hangman Online
        </Typography>
        <Box
          component="img"
          src="/images/right_leg.png"
          alt="Hangman"
          sx={{
    position: "relative",
    width: "80vw",
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "flex-start",
    gap: 6,
    px: 2,
    py: 6,
    overflowX: "hidden",
    background: "linear-gradient(-45deg, #1e1e2f, #2c2c3f, #3a3a4f, #1e1e2f)",
    backgroundSize: "400% 400%",
    animation: `${darkGradientAnimation} 20s ease infinite`,
  }}
        />
      </Box>

      {/* Room Controls */}
      <Card sx={{ ...glassCardStyle, maxWidth: 400, width: "100%" }}>
        <CardContent sx={{ display: "flex", flexDirection: "column", gap: 3 }}>
          <Button
            variant="contained"
            color="secondary"
            sx={{
              fontWeight: "bold",
              borderRadius: 4,
              py: 1.5,
              transition: "all 0.3s ease",
              "&:hover": { transform: "scale(1.05)", boxShadow: "0px 6px 20px rgba(0,0,0,0.4)" },
            }}
            onClick={handleCreateRoom}
          >
            Create Room
          </Button>

          <TextField
            placeholder="Enter Room ID"
            value={roomIdInput}
            onChange={(e) => setRoomIdInput(e.target.value)}
            sx={{
              background: "rgba(255,255,255,0.1)",
              borderRadius: 2,
              input: { color: "#fff" },
              "& .MuiOutlinedInput-notchedOutline": { borderColor: "rgba(255,255,255,0.3)" },
              "&:hover .MuiOutlinedInput-notchedOutline": { borderColor: "#fff" },
            }}
          />

          <Button
            variant="contained"
            color="primary"
            sx={{
              fontWeight: "bold",
              borderRadius: 4,
              py: 1.5,
              transition: "all 0.3s ease",
              "&:hover": { transform: "scale(1.05)", boxShadow: "0px 6px 20px rgba(0,0,0,0.4)" },
            }}
            onClick={handleJoinRoom}
          >
            Join Room
          </Button>
        </CardContent>
      </Card>

      {/* How to Play */}
      <Card sx={{ ...glassCardStyle, maxWidth: 600, width: "100%" }}>
        <CardContent>
          <Typography
            variant="h4"
            gutterBottom
            sx={{ color: "#ff4081", fontWeight: "bold" }}
          >
            How to Play Hangman
          </Typography>
          <Typography variant="body1" sx={{ color: "#fff", lineHeight: 1.8 }}>
            1. One player sets a secret word. <br />
            2. Other players guess letters one by one. <br />
            3. Each wrong guess loses a life. <br />
            4. Complete the word before you run out of lives! <br />
            Have fun with friends or solo!
          </Typography>
        </CardContent>
      </Card>

      {/* Feedback */}
      <Card sx={{ ...glassCardStyle, maxWidth: 600, width: "100%" }}>
        <CardContent>
          <Typography
            variant="h4"
            gutterBottom
            sx={{ color: "#ff4081", fontWeight: "bold" }}
          >
            Feedback
          </Typography>
          <form onSubmit={handleFeedbackSubmit}>
            <Grid container spacing={2}>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Name"
                  value={feedback.name}
                  onChange={(e) => setFeedback({ ...feedback, name: e.target.value })}
                  required
                  sx={{
                    input: { color: "#fff" },
                    "& .MuiOutlinedInput-notchedOutline": { borderColor: "rgba(255,255,255,0.3)" },
                  }}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Email"
                  type="email"
                  value={feedback.email}
                  onChange={(e) => setFeedback({ ...feedback, email: e.target.value })}
                  required
                  sx={{
                    input: { color: "#fff" },
                    "& .MuiOutlinedInput-notchedOutline": { borderColor: "rgba(255,255,255,0.3)" },
                  }}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Message"
                  value={feedback.message}
                  onChange={(e) => setFeedback({ ...feedback, message: e.target.value })}
                  required
                  sx={{
                    input: { color: "#fff" },
                    "& .MuiOutlinedInput-notchedOutline": { borderColor: "rgba(255,255,255,0.3)" },
                  }}
                />
              </Grid>
            </Grid>
            <Button
              type="submit"
              variant="contained"
              color="secondary"
              sx={{
                mt: 3,
                fontWeight: "bold",
                borderRadius: 4,
                py: 1.5,
                px: 4,
                transition: "all 0.3s ease",
                "&:hover": { transform: "scale(1.05)" },
              }}
            >
              {status}
            </Button>
          </form>
        </CardContent>
      </Card>
    </Box>
  );
};

export default Home;
