import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const BASE_URL = process.env.REACT_APP_SERVER_URL || "http://localhost:5000";

export default function CreateRoom() {
  const [hostName, setHostName] = useState("");
  const [roomData, setRoomData] = useState(null);
  const navigate = useNavigate();

  const handleCreate = async () => {
    if (!hostName.trim()) return alert("Enter your name");

    try {
      const res = await axios.post(`${BASE_URL}/api/create-room`, {
        hostName,
        totalRounds: 5,
        turnDuration: 60,
        maxStrikes: 6,
      });
      setRoomData(res.data);
    } catch (err) {
      console.error(err);
      alert("Failed to create room");
    }
  };

  const goToRoom = () => {
    if (roomData) {
      navigate(`/room/${roomData.roomId}`, {
        state: { playerId: roomData.hostId, playerName: roomData.hostName },
      });
    }
  };

  return (
    <div>
      <h2>Create Room</h2>
      {!roomData ? (
        <>
          <input
            type="text"
            placeholder="Your name"
            value={hostName}
            onChange={(e) => setHostName(e.target.value)}
          />
          <button onClick={handleCreate}>Create Room</button>
        </>
      ) : (
        <>
          <p>
            Room ID: <b>{roomData.roomId}</b>
          </p>
          <p>
            Invite Link:{" "}
            <a href={roomData.roomLink} target="_blank" rel="noreferrer">
              {roomData.roomLink}
            </a>
          </p>
          <button onClick={goToRoom}>Enter Room</button>
        </>
      )}
    </div>
  );
}
