import React from 'react';

function WordDisplay({ revealedWord, guessedLetters }) {
  return (
    <div className="space-y-4">
      <div className="flex flex-wrap justify-center gap-2">
        {revealedWord.map((letter, index) => (
          <div key={index} className="w-12 h-16 flex items-center justify-center border-b-4 border-gray-800 text-3xl font-bold text-gray-800">{letter}</div>
        ))}
      </div>

      <div className="bg-gray-50 p-2 rounded shadow">
        <h4 className="text-center font-semibold text-gray-600 mb-1">Guessed Letters</h4>
        <div className="flex flex-wrap justify-center gap-1">
          {guessedLetters.length > 0 ? guessedLetters.map((letter, idx) => (
            <span key={idx} className="px-2 py-1 bg-gray-200 rounded font-semibold">{letter}</span>
          )) : <span className="text-gray-400 italic text-sm">None yet</span>}
        </div>
      </div>
    </div>
  );
}

export default WordDisplay;
