import React, { useState } from "react";
import "./HomePage.css";

function HomePage({ onCreateRoom, onJoinRoom }) {
  const [roomCode, setRoomCode] = useState("");
  const [showHowToPlay, setShowHowToPlay] = useState(false);
  const [showFeedback, setShowFeedback] = useState(false);

  // NOW USED âœ”ï¸
  const [status, setStatus] = useState("");

  const handleJoin = () => {
    if (!roomCode.trim()) {
      setStatus("Please enter a valid room code.");
      return;
    }
    setStatus("Joining room...");
    onJoinRoom(roomCode);
  };

  const handleCreate = () => {
    setStatus("Creating room...");
    onCreateRoom();
  };

  const handleFeedbackSubmit = (e) => {
    e.preventDefault();
    const value = e.target.elements.feedback.value.trim();

    if (!value) {
      setStatus("Feedback cannot be empty.");
      return;
    }

    setStatus("Thank you for your feedback!");
    setShowFeedback(false);
  };

  return (
    <div className="home-container">
      <h1 className="title">ðŸŽ® Multiplayer Hangman</h1>

      {/* STATUS MESSAGE (NEW) */}
      {status && <p className="status-msg">{status}</p>}

      <div className="menu-box">
        <button className="btn-primary" onClick={handleCreate}>
          Create Room
        </button>

        <div className="join-section">
          <input
            type="text"
            className="room-input"
            placeholder="Enter Room Code"
            value={roomCode}
            onChange={(e) => setRoomCode(e.target.value)}
          />
          <button className="btn-secondary" onClick={handleJoin}>
            Join Room
          </button>
        </div>

        <button
          className="btn-tertiary"
          onClick={() => setShowHowToPlay(true)}
        >
          How to Play
        </button>

        <button className="btn-tertiary" onClick={() => setShowFeedback(true)}>
          Feedback
        </button>
      </div>

      {/* HOW TO PLAY POPUP */}
      {showHowToPlay && (
        <div className="popup">
          <div className="popup-box">
            <h2>How to Play</h2>
            <p>â€¢ One player sets a word.</p>
            <p>â€¢ Others guess letters.</p>
            <p>â€¢ Wrong guesses draw the hangman.</p>
            <p>â€¢ Guess the word before the man is fully drawn!</p>

            <button
              className="btn-close"
              onClick={() => setShowHowToPlay(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* FEEDBACK POPUP */}
      {showFeedback && (
        <div className="popup">
          <div className="popup-box">
            <h2>Feedback</h2>
            <form onSubmit={handleFeedbackSubmit}>
              <textarea
                name="feedback"
                className="feedback-box"
                placeholder="Write your feedback..."
              ></textarea>

              <button type="submit" className="btn-primary">
                Submit
              </button>
            </form>

            <button
              className="btn-close"
              onClick={() => setShowFeedback(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default HomePage;
