import React, { useState, useEffect, useCallback } from "react";
import {
  Box,
  Card,
  CardContent,
  Typography,
  FormGroup,
  TextField,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
  Button,
} from "@mui/material";
import { socket } from "../modules/socket";
import { Navigate } from "react-router-dom";

function Create({ user, setUser }) {
  const [roomID, setRoomID] = useState("");
  const [submitted, setSubmitted] = useState(false);

  const [state, setState] = useState({
    username: "",
    lives: "6",
    numRounds: "3",
    rotation: "king",
    time: "30",
  });

  // Handle room link from server
  const handleLink = useCallback(
    ({ gameState, roomID }) => {
      setUser(gameState.players[0]);
      setRoomID(roomID);
    },
    [setUser]
  );

  useEffect(() => {
    socket.on("link", handleLink);
    return () => socket.off("link", handleLink);
  }, [handleLink]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!state.username.trim()) return;
    setSubmitted(true);
    socket.emit("create", state);
  };

  const validateUsername = (name) => {
    if (!/^[^\s]+(\s+[^\s]+)*$/.test(name)) return "No leading/trailing spaces";
    if (name.length > 20) return "Max 20 characters";
    return "";
  };

  if (user && roomID) return <Navigate to={`/${roomID}`} />;

  return (
    <Box
      sx={{
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        px: 2,
        py: 6,
        backgroundColor: "#f9f9f9",
      }}
    >
      {/* Game Creation Card */}
      <Card
        sx={{
          width: "100%",
          maxWidth: 700, // Increased max width
          boxShadow: 3,
          p: 3,
        }}
      >
        <CardContent>
          <Typography
            variant="h4"
            gutterBottom
            sx={{ fontWeight: "bold", textAlign: "center" }}
          >
            Create Hangman Game
          </Typography>

          <form onSubmit={handleSubmit}>
            <FormGroup>
              <TextField
                label="Username"
                variant="filled"
                value={state.username}
                onChange={(e) =>
                  setState({ ...state, username: e.target.value })
                }
                error={!!validateUsername(state.username)}
                helperText={validateUsername(state.username)}
                disabled={submitted}
                required
                margin="normal"
              />

              <TextField
                label="Lives"
                type="number"
                variant="filled"
                value={state.lives}
                onChange={(e) => setState({ ...state, lives: e.target.value })}
                inputProps={{ min: 6, max: 10 }}
                disabled={submitted}
                required
                margin="normal"
              />

              <TextField
                label="Rounds"
                type="number"
                variant="filled"
                value={state.numRounds}
                onChange={(e) =>
                  setState({ ...state, numRounds: e.target.value })
                }
                inputProps={{ min: 1 }}
                disabled={submitted}
                required
                margin="normal"
              />

              <FormControl variant="filled" margin="normal">
                <InputLabel>Rotation</InputLabel>
                <Select
                  value={state.rotation}
                  onChange={(e) =>
                    setState({ ...state, rotation: e.target.value })
                  }
                  disabled={submitted}
                  required
                >
                  <MenuItem value="king">King of the Hill</MenuItem>
                  <MenuItem value="robin">Round Robin</MenuItem>
                </Select>
              </FormControl>

              <FormControl variant="filled" margin="normal">
                <InputLabel>Guess Time (sec)</InputLabel>
                <Select
                  value={state.time}
                  onChange={(e) =>
                    setState({ ...state, time: e.target.value })
                  }
                  disabled={submitted}
                  required
                >
                  {["10", "20", "30", "40", "50", "60", "70", "80", "90", "inf"].map(
                    (t) => (
                      <MenuItem key={t} value={t}>
                        {t}
                      </MenuItem>
                    )
                  )}
                </Select>
              </FormControl>

              <Button
                type="submit"
                variant="contained"
                color="primary"
                sx={{ mt: 2 }}
                disabled={submitted || !!validateUsername(state.username)}
              >
                Create Game
              </Button>
            </FormGroup>
          </form>
        </CardContent>
      </Card>
    </Box>
  );
}

export default Create;
