import React, { useState, useEffect } from 'react';
import HangmanCanvas from './HangmanCanvas';
import WordDisplay from './WordDisplay';
import PlayerList from './PlayerList';
import Chat from './Chat';
import GameOver from './GameOver';

function GameRoom({ socket, playerData, roomData, onLeave }) {
  const [gameState, setGameState] = useState(null);
  const [isHost, setIsHost] = useState(false);
  const [wordInput, setWordInput] = useState('');
  const [notification, setNotification] = useState('');
  const [showGameOver, setShowGameOver] = useState(false);
  const [leaderboard, setLeaderboard] = useState([]);

  useEffect(() => {
    // Join the room
    socket.emit('join-room', {
      roomId: roomData.roomId,
      playerId: playerData.playerId || playerData.hostId,
      playerName: playerData.playerName || playerData.hostName
    });

    socket.on('joined-room', (data) => setIsHost(data.isHost));
    socket.on('game-state', (state) => setGameState(state));
    socket.on('player-joined', (data) => showNotification(`${data.playerName} joined`));
    socket.on('player-left', (data) => showNotification(`${data.playerName} left`));
    socket.on('game-started', (state) => {
      setGameState(state);
      showNotification('Game started! Waiting for word setter...');
    });
    socket.on('waiting-for-word', (state) => {
      setGameState(state);
      showNotification('Waiting for word setter...');
    });
    socket.on('round-started', (state) => {
      setGameState(state);
      showNotification('Round started! Start guessing!');
    });
    socket.on('correct-guess', (data) => showNotification(`${data.playerName} guessed "${data.letter}" correctly!`));
    socket.on('wrong-guess', (data) => showNotification(`${data.playerName} guessed "${data.letter}" wrong! (${data.totalStrikes}/6 strikes)`));
    socket.on('turn-timeout', (data) => showNotification(`${data.playerName} ran out of time! (${data.totalStrikes}/6 strikes)`));
    socket.on('round-won', (data) => showNotification(`üéâ ${data.winnerName} won! Word: "${data.secretWord}". Points: ${data.points}`));
    socket.on('round-lost', (data) => showNotification(`üíÄ Round lost! Word: "${data.secretWord}"`));
    socket.on('game-over', (data) => {
      setLeaderboard(data.leaderboard);
      setShowGameOver(true);
    });
    socket.on('error', (data) => showNotification(`‚ùå ${data.message}`));

    return () => {
      socket.off();
    };
  }, [socket, playerData, roomData]);

  const showNotification = (message) => {
    setNotification(message);
    setTimeout(() => setNotification(''), 4000);
  };

  const handleStartGame = () => {
    socket.emit('start-game', {
      roomId: roomData.roomId,
      playerId: playerData.playerId || playerData.hostId
    });
  };

  const handleSetWord = (e) => {
    e.preventDefault();
    if (wordInput.trim().length < 3) {
      showNotification('Word must be at least 3 letters');
      return;
    }
    socket.emit('set-word', {
      roomId: roomData.roomId,
      playerId: playerData.playerId || playerData.hostId,
      word: wordInput.trim()
    });
    setWordInput('');
  };

  const handleNextRound = () => {
    socket.emit('next-round', { roomId: roomData.roomId });
    setShowGameOver(false);
  };

  const handleCopyInviteLink = () => {
    const inviteLink = `${window.location.origin}?join=${roomData.roomId}`;
    navigator.clipboard.writeText(inviteLink);
    showNotification('Invite link copied!');
  };

  if (showGameOver) {
    return (
      <GameOver
        leaderboard={leaderboard}
        onNextRound={handleNextRound}
        onLeave={onLeave}
        currentRound={gameState?.currentRound}
        totalRounds={gameState?.totalRounds}
      />
    );
  }

  if (!gameState) return <div className="loading">Loading...</div>;

  const currentPlayer = gameState.players.find(p => p.id === (playerData.playerId || playerData.hostId));
  const wordSetter = gameState.players[gameState.wordSetterIndex];
  const currentTurnPlayer = gameState.players[gameState.currentTurnIndex];
  const isMyTurn = currentTurnPlayer?.id === currentPlayer?.id;
  const isWordSetter = wordSetter?.id === currentPlayer?.id;
  const totalStrikes = gameState.players.reduce((sum, p) => p.id !== wordSetter?.id ? sum + p.strikes : sum, 0);

  return (
    <div className="game-room">
      <div className="game-header flex justify-between items-center mb-4">
        <div>
          <h2>Room: {roomData.roomId}</h2>
          <button className="btn btn-sm mt-1" onClick={handleCopyInviteLink}>üìã Copy Invite</button>
        </div>
        <div>
          <h3>Round {gameState.currentRound} / {gameState.totalRounds}</h3>
          <button className="btn btn-danger btn-sm" onClick={onLeave}>Leave</button>
        </div>
      </div>

      {notification && <div className="notification p-2 bg-yellow-100 text-yellow-800 rounded mb-3">{notification}</div>}

      <div className="flex gap-6">
        {/* LEFT PANEL */}
        <div className="flex-1 space-y-4">
          <PlayerList
            players={gameState.players}
            currentPlayerId={currentPlayer?.id}
            wordSetterId={wordSetter?.id}
            currentTurnId={currentTurnPlayer?.id}
          />

          {!gameState.gameStarted && isHost && (
            <button className="btn btn-primary w-full" onClick={handleStartGame} disabled={gameState.players.length < 2}>
              Start Game
            </button>
          )}

          {gameState.gameStarted && !gameState.roundInProgress && isWordSetter && (
            <form onSubmit={handleSetWord}>
              <input
                type="text"
                value={wordInput}
                onChange={(e) => setWordInput(e.target.value)}
                placeholder="Enter secret word"
                className="border px-2 py-1 w-full rounded"
                maxLength={20}
              />
              <button type="submit" className="btn btn-primary w-full mt-2">Set Word</button>
            </form>
          )}

          {gameState.roundInProgress && !isWordSetter && (
            <div>
              <div className="alphabet-grid grid grid-cols-7 gap-1">
                {'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('').map(letter => (
                  <button
                    key={letter}
                    className={`letter-btn ${gameState.guessedLetters.includes(letter) ? 'used' : ''}`}
                    onClick={() => {
                      if (isMyTurn && !gameState.guessedLetters.includes(letter)) {
                        socket.emit('guess-letter', {
                          roomId: roomData.roomId,
                          playerId: currentPlayer.id,
                          letter
                        });
                      }
                    }}
                    disabled={!isMyTurn || gameState.guessedLetters.includes(letter)}
                  >
                    {letter}
                  </button>
                ))}
              </div>
              {isMyTurn && <p className="text-green-600 font-bold mt-2">üéØ Your Turn!</p>}
            </div>
          )}
        </div>

        {/* CENTER PANEL */}
        <div className="flex-1 flex flex-col items-center">
          <HangmanCanvas strikes={totalStrikes} maxStrikes={6} />
          {gameState.roundInProgress && (
            <WordDisplay revealedWord={gameState.revealedWord} guessedLetters={gameState.guessedLetters} />
          )}
          {gameState.roundInProgress && (
            <div className="mt-3 text-center">
              <p>‚ùå Strikes: {totalStrikes} / 6</p>
              <p>Current Turn: <span className="font-bold">{currentTurnPlayer?.name}</span></p>
              <p>Word Setter: <span className="font-bold">{wordSetter?.name}</span></p>
            </div>
          )}
        </div>

        {/* RIGHT PANEL */}
        <div className="flex-1">
          <Chat socket={socket} roomId={roomData.roomId} playerId={currentPlayer?.id} messages={gameState.messages} />
        </div>
      </div>
    </div>
  );
}

export default GameRoom;
