import React, { useEffect, useState } from "react";
import { useNavigate, Navigate } from "react-router-dom";
import { FaPaperPlane } from "react-icons/fa";
import axios from "axios";

function Create({ socket, user, roomID, setUser, setFeedback }) {

  const [username, setUsername] = useState("");
  const [usernameError, setUsernameError] = useState(false);

  const [feedback, setFeedbackText] = useState("");
  const [feedbackError, setFeedbackError] = useState(false);
  const [feedbackSuccess, setFeedbackSuccess] = useState(false);

  const [CreationID, setCreationID] = useState(null);

  useEffect(() => {
  if (!socket) return;

  socket.on("link", (data) => {
    setUser(data.username);
    setCreationID(data.roomID);
  });

  return () => {
    socket.off("link");
  };
}, [socket, setUser]);

  // Auto redirect once room is created (option A)
  if (user && CreationID) {
    return <Navigate to={`/${CreationID}`} />;
  }

  const handleCreateRoom = () => {
    if (!username.trim()) {
      setUsernameError(true);
      return;
    }
    setUsernameError(false);

    socket.emit("create", username);
  };

  const handleFeedbackSubmit = () => {
    if (!feedback.trim()) {
      setFeedbackError(true);
      return;
    }

    setFeedbackError(false);

    axios
      .post("https://hangman-backend-l926.onrender.com/feedback", { feedback })
      .then(() => {
        setFeedbackSuccess(true);
        setFeedbackError(false);
        setFeedback(feedback);
      })
      .catch(() => {
        setFeedbackSuccess(false);
        setFeedbackError(true);
      });
  };

  return (
    <div className="flex items-center justify-center min-h-screen flex-col text-white bg-black">
      <h1 className="text-4xl sm:text-5xl lg:text-6xl font-bold">
        CREATE ROOM
      </h1>

      <div className="border border-gray-700 rounded-xl p-6 w-96 sm:w-[28rem] max-w-full mt-8 bg-gray-800 shadow-xl">
        {/* Username Input */}
        <label className="block text-left text-lg font-light ml-1">
          Username
        </label>
        <input
          type="text"
          maxLength={12}
          value={username}
          onChange={(e) => {
            setUsername(e.target.value);
            setUsernameError(false);
          }}
          placeholder="Enter your username"
          className="w-full p-3 bg-gray-900 rounded-lg border border-transparent focus:border-gray-500 outline-none mt-2 text-white"
        />
        {usernameError && (
          <p className="text-red-500 mt-1 text-sm">Please enter your username</p>
        )}

        {/* Create Room Button */}
        <button
          onClick={handleCreateRoom}
          className="w-full mt-4 p-3 flex justify-center bg-blue-600 hover:bg-blue-700 transition rounded-lg"
        >
          Create Room
        </button>
      </div>

      {/* Feedback Box */}
      <div className="border border-gray-700 rounded-xl p-6 w-96 sm:w-[28rem] max-w-full mt-10 bg-gray-800 shadow-xl">
        <label className="block text-left text-lg font-light ml-1">
          Feedback
        </label>
        <textarea
          maxLength={60}
          value={feedback}
          onChange={(e) => {
            setFeedbackText(e.target.value);
            setFeedbackError(false);
          }}
          placeholder="Give us your feedback"
          className="w-full p-3 bg-gray-900 rounded-lg border border-transparent focus:border-gray-500 outline-none mt-2 text-white min-h-[100px] resize-none"
        />

        <div className="flex justify-end mt-3">
          <button
            onClick={handleFeedbackSubmit}
            className="p-3 bg-blue-600 hover:bg-blue-700 transition rounded-xl"
          >
            <FaPaperPlane size={20} />
          </button>
        </div>

        {feedbackError && (
          <p className="text-red-500 mt-1 text-sm">
            Feedback cannot be empty.
          </p>
        )}
        {feedbackSuccess && (
          <p className="text-green-400 mt-1 text-sm">Feedback more Comingâ€¦</p>
        )}
      </div>
    </div>
  );
}

export default Create;
