import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import Game from "./Game";
import Wait from "./Wait";
import Chat from "./Chat";
import NewWord from "./NewWord";
import axios from "axios";
import "../css/Room.scss";
import { socket } from "../modules/socket";
import { Box, Typography, List, ListItem, ListItemText } from "@mui/material";

function Room({ username, mute }) {
  const [gameState, setGameState] = useState();
  const [user, setUser] = useState(username);
  const { roomID } = useParams();
  const [err, setErr] = useState(false);

  const handleLeave = useCallback((newState) => {
    if (newState && newState.players.length === 0) setErr(true);
    setGameState({ ...newState });
  }, []);

  useEffect(() => {
    const getGameState = async () => {
      try {
        const backendURL = process.env.REACT_APP_SERVER || "http://localhost:5000";
        const res = await axios.get(`${backendURL}/?roomID=${roomID}`);
        if (res.status === 200) setGameState(res.data);
      } catch (error) {
        setErr(true);
      }
    };

    socket.on("leave", handleLeave);
    getGameState();

    return () => {
      socket.off("leave", handleLeave);
    };
  }, [roomID, handleLeave]);

  useEffect(() => {
    const cleanup = () => {
      socket.emit("leave", { user, roomID });
    };

    window.addEventListener("unload", cleanup);
    return () => window.removeEventListener("unload", cleanup);
  }, [user, roomID]);

  const renderContent = () => {
    if (err) return <p>Room does not exist</p>;
    if (!gameState) return <p>Loading...</p>;

    if (user === "" || !gameState.gameStart) {
      return (
        <Wait
          user={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          setUser={setUser}
          mute={mute}
        />
      );
    }

    if (gameState.gameStart && gameState.category !== "") {
      return (
        <Game
          username={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          mute={mute}
        />
      );
    }

    if (gameState.category === "") {
      return (
        <NewWord
          gameState={gameState}
          setGameState={setGameState}
          user={user}
          roomID={roomID}
          mute={mute}
        />
      );
    }

    return <p>Loading...</p>;
  };

  const showChat = () => {
    if (
      gameState &&
      Array.isArray(gameState.players) &&
      user &&
      gameState.players.includes(user) &&
      gameState.players.length > 1
    ) {
      return <Chat user={user} roomID={roomID} />;
    }
    return null;
  };

  return (
    <Box className="room-container" sx={{ display: "flex", gap: 2, p: 2 }}>
      {/* Left column: Room info and players */}
      <Box
        sx={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          gap: 2,
          maxWidth: 300,
        }}
      >
        <Box sx={{ bgcolor: "#1976d2", p: 2, borderRadius: 1 }}>
          <Typography variant="h6" fontWeight="bold" textAlign="center" color="#fff">
            ðŸŽ® Room Code: {roomID.toUpperCase()}
          </Typography>
        </Box>

        <Box sx={{ bgcolor: "#f0f0f0", p: 2, borderRadius: 1 }}>
          <Typography variant="subtitle1" fontWeight="bold">
            Players
          </Typography>
          <List dense>
            {gameState?.players?.map((p, index) => (
              <ListItem key={p}>
                <ListItemText primary={`${index + 1}. ${p}`} />
              </ListItem>
            ))}
          </List>
        </Box>
      </Box>

      {/* Right column: Main content */}
      <Box sx={{ flex: 2 }}>{renderContent()}</Box>

      {/* Chat panel */}
      {showChat()}
    </Box>
  );
}

export default Room;
