import React, { useState } from "react";

export default function Chat({ socket, playerId, messages }) {
  const [text, setText] = useState("");

  const sendMessage = () => {
    if (!text.trim()) return;
    socket.emit("send-message", { roomId: messages.roomId, playerId, message: text });
    setText("");
  };

  return (
    <div>
      <h4>Chat</h4>
      <div style={{ maxHeight: 200, overflowY: "scroll" }}>
        {messages.map((m) => (
          <div key={m.id}>
            <b>{m.playerName}:</b> {m.message}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Type message"
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
}
