import React, { useState, useEffect, useRef } from "react";

function Chat({ socket, roomId, playerId, messages }) {
  const [messageInput, setMessageInput] = useState("");
  const [localMessages, setLocalMessages] = useState(messages || []);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    setLocalMessages(messages || []);
  }, [messages]);

  useEffect(() => {
    const handleNewMessage = (message) => {
      setLocalMessages((prev) => [...prev, message]);
    };

    socket.on("new-message", handleNewMessage);

    return () => socket.off("new-message", handleNewMessage);
  }, [socket]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [localMessages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!messageInput.trim()) return;

    socket.emit("send-message", {
      roomId,
      playerId,
      message: messageInput.trim(),
    });

    setMessageInput("");
  };

  return (
    <div className="w-full h-full flex flex-col bg-gray-900 border-l border-gray-700">

      {/* Header */}
      <div className="p-3 bg-gray-800 border-b border-gray-700">
        <h3 className="text-lg font-semibold text-white">Chat</h3>
      </div>

      {/* Chat Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3">
        {localMessages.length === 0 ? (
          <div className="text-gray-400 text-center mt-6">
            No messages yet...
          </div>
        ) : (
          localMessages.map((msg) => {
            const isOwn = msg.playerId === playerId;
            return (
              <div
                key={msg.id}
                className={`flex flex-col ${
                  isOwn ? "items-end" : "items-start"
                }`}
              >
                {/* Author & Time */}
                <div className="text-xs text-gray-400 mb-1 px-1">
                  {isOwn ? "You" : msg.playerName} Â·{" "}
                  {new Date(msg.timestamp).toLocaleTimeString([], {
                    hour: "2-digit",
                    minute: "2-digit",
                  })}
                </div>

                {/* Message Bubble */}
                <div
                  className={`px-4 py-2 rounded-2xl max-w-[75%] text-white ${
                    isOwn
                      ? "bg-blue-600 rounded-br-none"
                      : "bg-gray-700 rounded-bl-none"
                  }`}
                >
                  {msg.message}
                </div>
              </div>
            );
          })
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Message Input */}
      <form
        onSubmit={handleSendMessage}
        className="p-3 bg-gray-800 border-t border-gray-700 flex gap-2"
      >
        <input
          type="text"
          value={messageInput}
          onChange={(e) => setMessageInput(e.target.value)}
          placeholder="Type a message..."
          maxLength={200}
          className="flex-1 px-4 py-2 rounded-xl bg-gray-700 text-white border border-gray-600 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          type="submit"
          className="px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-xl text-white font-semibold transition"
        >
          Send
        </button>
      </form>
    </div>
  );
}

export default Chat;
