import React, { useEffect, useState } from "react";
import { useLocation, useParams } from "react-router-dom";
import io from "socket.io-client";
import PlayerList from "./PlayerList";
import GameRoom from "./GameRoom";

const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || "http://localhost:5000";

export default function Lobby() {
  const { roomId } = useParams();
  const location = useLocation();
  const [socket, setSocket] = useState(null);
  const [playerId, setPlayerId] = useState(location.state?.playerId || null);
  const [playerName] = useState(location.state?.playerName || "Guest");
  const [roomState, setRoomState] = useState(null);

  useEffect(() => {
    const s = io(SOCKET_URL);
    setSocket(s);

    s.emit("join-room", { roomId, playerId, playerName }, (res) => {
      if (!res.success) alert(res.message || "Failed to join room");
    });

    s.on("game-state", (state) => setRoomState(state));
    s.on("joined-room", (data) => setPlayerId(data.playerId));

    return () => s.disconnect();
  }, [roomId, playerId, playerName]);

  if (!socket || !roomState) return <p>Loading room...</p>;

  return (
    <div>
      <h2 className="text-xl mb-2">Room ID: {roomId}</h2>
      {!roomState.gameStarted ? (
        <div>
          <PlayerList players={roomState.players} />
          {roomState.players.find(p => p.id === playerId)?.isHost && (
            <button onClick={() => socket.emit("start-game", { roomId, playerId })}>Start Game</button>
          )}
        </div>
      ) : (
        <GameRoom socket={socket} playerId={playerId} roomState={roomState} />
      )}
    </div>
  );
}
