import React, { useEffect, useRef } from 'react';

function HangmanCanvas({ strikes, maxStrikes }) {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.strokeStyle = '#333';
    ctx.lineWidth = 3;
    ctx.lineCap = 'round';

    // Draw gallows
    ctx.beginPath();
    ctx.moveTo(50, 250);
    ctx.lineTo(150, 250);
    ctx.stroke();

    ctx.beginPath();
    ctx.moveTo(100, 250);
    ctx.lineTo(100, 50);
    ctx.stroke();

    ctx.beginPath();
    ctx.moveTo(100, 50);
    ctx.lineTo(200, 50);
    ctx.stroke();

    ctx.beginPath();
    ctx.moveTo(200, 50);
    ctx.lineTo(200, 80);
    ctx.stroke();

    // Calculate which parts to draw based on maxStrikes
    const parts = [];
    
    if (maxStrikes === 6) {
      parts.push('head', 'body', 'leftArm', 'rightArm', 'leftLeg', 'rightLeg');
    } else if (maxStrikes === 7) {
      parts.push('head', 'body', 'leftArm', 'rightArm', 'leftLeg', 'rightLeg', 'leftEye');
    } else if (maxStrikes === 8) {
      parts.push('head', 'body', 'leftArm', 'rightArm', 'leftLeg', 'rightLeg', 'leftEye', 'rightEye');
    } else if (maxStrikes === 9) {
      parts.push('head', 'body', 'leftArm', 'rightArm', 'leftLeg', 'rightLeg', 'leftEye', 'rightEye', 'mouth');
    } else if (maxStrikes === 10) {
      parts.push('head', 'body', 'leftArm', 'rightArm', 'leftLeg', 'rightLeg', 'leftEye', 'rightEye', 'mouth', 'nose');
    }

    // Draw hangman parts based on strikes
    for (let i = 0; i < strikes && i < parts.length; i++) {
      const part = parts[i];
      
      switch(part) {
        case 'head':
          ctx.beginPath();
          ctx.arc(200, 100, 20, 0, Math.PI * 2);
          ctx.stroke();
          break;
          
        case 'body':
          ctx.beginPath();
          ctx.moveTo(200, 120);
          ctx.lineTo(200, 180);
          ctx.stroke();
          break;
          
        case 'leftArm':
          ctx.beginPath();
          ctx.moveTo(200, 140);
          ctx.lineTo(170, 160);
          ctx.stroke();
          break;
          
        case 'rightArm':
          ctx.beginPath();
          ctx.moveTo(200, 140);
          ctx.lineTo(230, 160);
          ctx.stroke();
          break;
          
        case 'leftLeg':
          ctx.beginPath();
          ctx.moveTo(200, 180);
          ctx.lineTo(180, 220);
          ctx.stroke();
          break;
          
        case 'rightLeg':
          ctx.beginPath();
          ctx.moveTo(200, 180);
          ctx.lineTo(220, 220);
          ctx.stroke();
          break;
          
        case 'leftEye':
          ctx.beginPath();
          ctx.arc(195, 95, 2, 0, Math.PI * 2);
          ctx.fillStyle = '#333';
          ctx.fill();
          break;
          
        case 'rightEye':
          ctx.beginPath();
          ctx.arc(205, 95, 2, 0, Math.PI * 2);
          ctx.fillStyle = '#333';
          ctx.fill();
          break;
          
        case 'mouth':
          ctx.beginPath();
          ctx.arc(200, 105, 8, 0, Math.PI);
          ctx.stroke();
          break;
          
        case 'nose':
          ctx.beginPath();
          ctx.moveTo(200, 100);
          ctx.lineTo(198, 105);
          ctx.stroke();
          break;
          
        default:
          break;
      }
    }
  }, [strikes, maxStrikes]);

  return (
    <div className="bg-gray-50 rounded-2xl p-6 shadow-inner">
      <canvas 
        ref={canvasRef} 
        width={300} 
        height={300}
        className="mx-auto"
      />
    </div>
  );
}

export default HangmanCanvas;
