import React from "react";

function PlayerList({ players, currentPlayerId, wordSetterId, currentTurnId }) {
  return (
    <div className="w-full h-full bg-gray-900 border-r border-gray-700 p-4 flex flex-col">
      <h3 className="text-white text-xl font-semibold mb-4">Players</h3>

      <div className="space-y-3 overflow-y-auto">
        {players.map((player) => {
          const isYou = player.id === currentPlayerId;
          const isTurn = player.id === currentTurnId;
          const isWordSetter = player.id === wordSetterId;

          return (
            <div
              key={player.id}
              className={`
                w-full p-3 rounded-xl border flex flex-col transition
                ${isTurn ? "border-blue-500 bg-gray-800 shadow-lg shadow-blue-900/30" : "border-gray-700 bg-gray-800/60"}
              `}
            >
              {/* Top Row: Player Name + Badges */}
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  <span className="text-white font-semibold">
                    {player.name}
                  </span>

                  {/* Host Badge */}
                  {player.isHost && (
                    <span className="px-2 py-0.5 rounded-md bg-yellow-600 text-black text-xs font-bold">
                      üëë Host
                    </span>
                  )}

                  {/* Word Setter */}
                  {isWordSetter && (
                    <span className="px-2 py-0.5 rounded-md bg-purple-600 text-white text-xs font-semibold">
                      üìù Word Setter
                    </span>
                  )}

                  {/* You */}
                  {isYou && (
                    <span className="px-2 py-0.5 rounded-md bg-green-600 text-white text-xs font-semibold">
                      ‚≠ê You
                    </span>
                  )}

                  {/* Current Turn Indicator */}
                  {isTurn && (
                    <span className="px-2 py-0.5 rounded-md bg-blue-600 text-white text-xs font-semibold animate-pulse">
                      üî• Your Turn
                    </span>
                  )}
                </div>
              </div>

              {/* Score + Strikes */}
              <div className="flex items-center justify-between mt-2">
                <span className="text-yellow-400 font-bold">
                  üèÜ {player.score}
                </span>

                {/* Strikes - Only for guessers */}
                {!isWordSetter && player.strikes > 0 && (
                  <span className="text-red-400 font-semibold">
                    ‚ùå {player.strikes}
                  </span>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default PlayerList;
