import React, { useState, useEffect } from 'react';
import HangmanCanvas from './HangmanCanvas';
import WordDisplay from './WordDisplay';
import PlayerList from './PlayerList';
import Chat from './Chat';
import GameOver from './GameOver';

function GameRoom({ socket, playerData, roomData, onLeave }) {
  const [gameState, setGameState] = useState(null);
  const [isHost, setIsHost] = useState(false);
  const [wordInput, setWordInput] = useState('');
  const [letterInput, setLetterInput] = useState('');
  const [notification, setNotification] = useState('');
  const [showGameOver, setShowGameOver] = useState(false);
  const [leaderboard, setLeaderboard] = useState([]);

  // Ensure persistent playerId for multi-tab
  useEffect(() => {
    let playerId = playerData.playerId || localStorage.getItem('playerId');
    if (!playerId) {
      playerId = crypto.randomUUID();
      localStorage.setItem('playerId', playerId);
    }

    const playerName = playerData.playerName || playerData.hostName;

    socket.emit('join-room', { roomId: roomData.roomId, playerId, playerName });

    socket.on('joined-room', (data) => setIsHost(data.isHost));
    socket.on('game-state', setGameState);

    socket.on('player-joined', data => showNotification(`${data.playerName} joined`));
    socket.on('player-left', data => showNotification(`${data.playerName} left`));
    socket.on('game-started', state => {
      setGameState(state);
      showNotification('Game started! Waiting for word...');
    });
    socket.on('waiting-for-word', state => {
      setGameState(state);
      showNotification('Waiting for word setter...');
    });
    socket.on('round-started', state => {
      setGameState(state);
      showNotification('Round started! Start guessing!');
    });
    socket.on('correct-guess', data => showNotification(`${data.playerName} guessed "${data.letter}" correctly!`));
    socket.on('wrong-guess', data => showNotification(`${data.playerName} guessed "${data.letter}" wrong! (${data.totalStrikes}/6)`));
    socket.on('turn-timeout', data => showNotification(`${data.playerName} ran out of time! (${data.totalStrikes}/6)`));
    socket.on('round-won', data => showNotification(`ðŸŽ‰ ${data.winnerName} won! Word: "${data.secretWord}" (+${data.points} pts)`));
    socket.on('round-lost', data => showNotification(`ðŸ’€ Round lost! Word: "${data.secretWord}"`));
    socket.on('game-over', data => { setLeaderboard(data.leaderboard); setShowGameOver(true); });
    socket.on('error', data => showNotification(`âŒ ${data.message}`));

    return () => {
      socket.off();
    };
  }, [socket, playerData, roomData]);

  const showNotification = (msg) => {
    setNotification(msg);
    setTimeout(() => setNotification(''), 4000);
  };

  const playerId = playerData.playerId || localStorage.getItem('playerId');
  const currentPlayer = gameState?.players.find(p => p.id === playerId);
  const wordSetter = gameState?.players[gameState?.wordSetterIndex];
  const currentTurnPlayer = gameState?.players[gameState?.currentTurnIndex];
  const isMyTurn = currentTurnPlayer?.id === currentPlayer?.id;
  const isWordSetter = wordSetter?.id === currentPlayer?.id;
  const totalStrikes = gameState?.players.reduce((sum, p) => p.id !== wordSetter?.id ? sum + p.strikes : sum, 0);

  if (showGameOver) {
    return <GameOver leaderboard={leaderboard} onNextRound={() => { socket.emit('next-round', { roomId: roomData.roomId }); setShowGameOver(false); }} onLeave={onLeave} currentRound={gameState?.currentRound} totalRounds={gameState?.totalRounds} />;
  }

  if (!gameState) return <div className="loading">Loading...</div>;

  return (
    <div className="game-room p-4 flex flex-col gap-4">
      <div className="flex justify-between items-center bg-gray-100 p-2 rounded-lg shadow">
        <div>
          <h2 className="font-bold text-lg">Room: {roomData.roomId}</h2>
          <p className="text-sm">Round {gameState.currentRound} / {gameState.totalRounds}</p>
        </div>
        <div className="flex gap-2">
          <button onClick={() => navigator.clipboard.writeText(`${window.location.origin}?join=${roomData.roomId}`)} className="btn btn-sm bg-indigo-500 text-white rounded px-2">ðŸ“‹ Copy Invite</button>
          <button onClick={onLeave} className="btn btn-sm bg-red-500 text-white rounded px-2">Leave</button>
        </div>
      </div>

      {notification && <div className="bg-yellow-100 p-2 rounded text-center font-semibold">{notification}</div>}

      <div className="flex flex-col md:flex-row gap-4">
        {/* Left Panel: Players & Actions */}
        <div className="md:w-1/4 space-y-4">
          <PlayerList players={gameState.players} currentPlayerId={playerId} wordSetterId={wordSetter?.id} currentTurnId={currentTurnPlayer?.id} />

          {!gameState.gameStarted && isHost && (
            <button onClick={() => socket.emit('start-game', { roomId: roomData.roomId, playerId })} className="btn w-full bg-green-500 text-white py-2 rounded" disabled={gameState.players.length < 2}>Start Game</button>
          )}

          {gameState.gameStarted && !gameState.roundInProgress && isWordSetter && (
            <form onSubmit={(e) => { e.preventDefault(); if (wordInput.length < 3) return showNotification('Word must be at least 3 letters'); socket.emit('set-word', { roomId: roomData.roomId, playerId, word: wordInput.trim() }); setWordInput(''); }}>
              <input value={wordInput} onChange={(e) => setWordInput(e.target.value)} placeholder="Set secret word" className="w-full p-2 border rounded mb-2" maxLength={20} />
              <button type="submit" className="w-full btn bg-purple-500 text-white rounded py-2">Set Word</button>
            </form>
          )}

          {gameState.roundInProgress && !isWordSetter && (
            <div className="grid grid-cols-7 gap-1">
              {'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('').map(letter => (
                <button key={letter} className={`p-2 rounded ${gameState.guessedLetters.includes(letter) ? 'bg-gray-300 cursor-not-allowed' : 'bg-indigo-200 hover:bg-indigo-400'} text-sm`} disabled={!isMyTurn || gameState.guessedLetters.includes(letter)} onClick={() => socket.emit('guess-letter', { roomId: roomData.roomId, playerId, letter })}>{letter}</button>
              ))}
            </div>
          )}
        </div>

        {/* Center Panel: Hangman Canvas & Word */}
        <div className="md:w-2/4 flex flex-col items-center gap-4">
          <HangmanCanvas strikes={totalStrikes} maxStrikes={6} />
          {gameState.roundInProgress && <WordDisplay revealedWord={gameState.revealedWord} guessedLetters={gameState.guessedLetters} />}
          <p className="mt-2 font-semibold">Turn: {currentTurnPlayer?.name} {isMyTurn && '(Your turn)'}</p>
          <p className="mt-1 text-red-600 font-semibold">Strikes: {totalStrikes} / 6</p>
        </div>

        {/* Right Panel: Chat */}
        <div className="md:w-1/4">
          <Chat socket={socket} roomId={roomData.roomId} playerId={playerId} messages={gameState.messages} />
        </div>
      </div>
    </div>
  );
}

export default GameRoom;
