import React from 'react';

const PlayerList = ({ players, currentTurnPlayerId, wordSetterId }) => {
  return (
    <div className="bg-white rounded-xl shadow-lg p-6">
      <h3 className="text-xl font-bold mb-4 text-gray-800">
        Players ({players.length})
      </h3>
      <div className="space-y-2">
        {players.map((player) => (
          <div
            key={player.id}
            className={`p-3 rounded-lg transition-all ${
              player.id === currentTurnPlayerId
                ? 'bg-green-100 border-2 border-green-500 transform scale-105'
                : 'bg-gray-50 border-2 border-transparent'
            }`}
          >
            <div className="flex justify-between items-start">
              <div className="flex-1">
                <div className="flex items-center gap-2">
                  <p className="font-semibold text-gray-800">{player.name}</p>
                  {player.id === currentTurnPlayerId && (
                    <span className="text-xs bg-green-500 text-white px-2 py-1 rounded animate-pulse">
                      TURN
                    </span>
                  )}
                </div>
                <div className="flex gap-2 mt-1">
                  {player.isHost && (
                    <span className="text-xs bg-yellow-400 text-gray-800 px-2 py-1 rounded font-medium">
                      HOST
                    </span>
                  )}
                  {player.id === wordSetterId && (
                    <span className="text-xs bg-purple-500 text-white px-2 py-1 rounded font-medium">
                      WORD SETTER
                    </span>
                  )}
                </div>
              </div>
              <div className="text-right">
                <p className="font-bold text-xl text-blue-600">{player.score}</p>
                <p className="text-xs text-gray-500">points</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PlayerList;
