import React, { useState, useEffect, useRef, useCallback } from "react";
import { useParams } from "react-router-dom";
import Game from "./Game";
import Wait from "./Wait";
import Chat from "./Chat";
import NewWord from "./NewWord";
import axios from "axios";
import "../css/Room.scss";
import { socket } from "../modules";

function Room({ username, mute }) {
  const [gameState, setGameState] = useState();
  const [user, setUser] = useState(username);
  const { roomID } = useParams();
  const [err, setErr] = useState(false);

  // audio removed entirely
  const audioRef = useRef(null);

  const handleLeave = useCallback((newState) => {
    if (newState && newState.players.length === 0) setErr(true);
    setGameState({ ...newState });
  }, []);

  useEffect(() => {
    const getGameState = async () => {
      let res = null;

      try {
        res = await axios.get(
          `${process.env.REACT_APP_SERVER}/?roomID=${roomID}`
        );
      } catch (error) {
        setErr(true);
      } finally {
        if (res && res.status === 200) {
          setGameState(res.data);
        }
      }
    };

    socket.on("leave", handleLeave);
    getGameState();

    return () => {
      socket.off("leave", handleLeave);
    };
  }, [roomID, handleLeave]);

  useEffect(() => {
    const cleanup = () => {
      socket.emit("leave", { user: user, roomID: roomID });
    };

    window.addEventListener("unload", cleanup);
    return () => window.removeEventListener("unload", cleanup);
  }, [user, roomID]);

  const renderContent = () => {
    if (err) {
      return <p>Room does not exist</p>;
    }

    if (!gameState) {
      return <p>Loading...</p>;
    }

    if (user === "" || !gameState.gameStart) {
      return (
        <Wait
          user={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          setUser={setUser}
          mute={mute}
        />
      );
    }

    if (gameState.gameStart && gameState.category !== "") {
      return (
        <Game
          username={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          mute={mute}
        />
      );
    }

    if (gameState.category === "") {
      return (
        <NewWord
          gameState={gameState}
          setGameState={setGameState}
          user={user}
          roomID={roomID}
          mute={mute}
        />
      );
    }

    return <p>Loading...</p>;
  };

  const showChat = () => {
    if (
      !err &&
      gameState &&
      gameState.players.includes(user) &&
      gameState.players.length > 1
    ) {
      return <Chat user={user} roomID={roomID} />;
    }
  };

  return (
    <div className="room-container">
      {renderContent()}
      {showChat()}
    </div>
  );
}

export default Room;
