import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import Game from "./Game";
import Wait from "./Wait";
import Chat from "./Chat";
import NewWord from "./NewWord";
import axios from "axios";
import "../css/Room.scss";
import { socket } from "../modules/socket";
import { Box, Typography, Paper, List, ListItem, ListItemText, Button, TextField } from "@mui/material";

function Room({ username: initialUsername, mute }) {
  const { roomID } = useParams();
  const [gameState, setGameState] = useState(null);
  const [username, setUsername] = useState(initialUsername || "");
  const [err, setErr] = useState(false);

  const handleLeave = useCallback((newState) => {
    if (!newState || !Array.isArray(newState.players) || newState.players.length === 0) {
      setErr(true);
    } else {
      setGameState({ ...newState });
    }
  }, []);

  useEffect(() => {
    const getGameState = async () => {
      try {
        const backendURL = process.env.REACT_APP_SERVER || "http://localhost:5000";
        const res = await axios.get(`${backendURL}/?roomID=${roomID}`);
        if (res.status === 200 && res.data && res.data.players) {
          setGameState(res.data);
        } else {
          setErr(true);
        }
      } catch (error) {
        setErr(true);
      }
    };

    socket.on("leave", handleLeave);
    getGameState();

    return () => socket.off("leave", handleLeave);
  }, [roomID, handleLeave]);

  useEffect(() => {
    const cleanup = () => {
      if (username) socket.emit("leave", { user: username, roomID });
    };
    window.addEventListener("unload", cleanup);
    return () => window.removeEventListener("unload", cleanup);
  }, [username, roomID]);

  const renderContent = () => {
    if (err) return <Typography color="error">Room does not exist</Typography>;
    if (!gameState) return <Typography>Loading...</Typography>;

    if (!username || !gameState.gameStart) {
      return <Wait user={username} roomID={roomID} gameState={gameState} setGameState={setGameState} setUser={setUsername} mute={mute} />;
    }

    if (gameState.gameStart && gameState.category !== "") {
      return <Game username={username} roomID={roomID} gameState={gameState} setGameState={setGameState} mute={mute} />;
    }

    if (gameState.category === "") {
      return <NewWord gameState={gameState} setGameState={setGameState} user={username} roomID={roomID} mute={mute} />;
    }

    return <Typography>Loading...</Typography>;
  };

  return (
    <Box
      sx={{
        display: "flex",
        height: "100vh",
        width: "100vw",
        px: 5, // gap from screen edges
        py: 2,
      }}
    >
      {/* Left Panel: Players + Room Link */}
      <Paper
        sx={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          gap: 2,
          p: 2,
          borderRadius: 2,
          overflowY: "auto",
          maxHeight: "100vh",
          alignItems: "center", // center contents horizontally
        }}
        elevation={3}
      >
        {/* Room Code / Share Link */}
        <Typography
          variant="h6"
          fontWeight="bold"
          textAlign="center"
          sx={{
            mb: 2,
            p: 1,
            bgcolor: "lightblue",
            borderRadius: 1,
            width: "100%",
          }}
        >
          ðŸŽ® Room Code: {roomID.toUpperCase()}
        </Typography>

        {/* Players List */}
        <Box sx={{ width: "100%" }}>
          <Typography variant="subtitle1" fontWeight="bold" textAlign="center">
            Players
          </Typography>
          <List dense>
            {gameState?.players?.map((p, idx) => (
              <ListItem key={p}>
                <ListItemText primary={`${idx + 1}. ${p}`} />
              </ListItem>
            ))}
          </List>
        </Box>
      </Paper>

      {/* Center Panel: Game / Wait / NewWord */}
      <Box sx={{ flex: 2, mx: 2, overflowY: "auto" }}>{renderContent()}</Box>

      {/* Right Panel: Chat */}
      <Paper
        sx={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          p: 2,
          borderRadius: 2,
          overflowY: "auto",
          maxHeight: "100vh",
        }}
        elevation={3}
      >
        <Typography variant="h6" fontWeight="bold" textAlign="center">
          ðŸ’¬ Chat
        </Typography>
        {username && <Chat user={username} roomID={roomID} />}
      </Paper>
    </Box>
  );
}

export default Room;
