import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import {
  Box,
  Button,
  Card,
  CardContent,
  TextField,
  Typography,
  Grid,
} from "@mui/material";
import axios from "axios";

const Home = () => {
  const navigate = useNavigate();
  const [roomIdInput, setRoomIdInput] = useState("");
  const [feedback, setFeedback] = useState({ name: "", email: "", message: "" });
  const [status, setStatus] = useState("SEND");
  const timerRef = useRef(null);

  const handleCreateRoom = () => {
    navigate("/create");
  };

  const handleJoinRoom = () => {
    if (roomIdInput.trim() !== "") {
      navigate(`/${roomIdInput}`);
    }
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    if (timerRef.current) clearTimeout(timerRef.current);
    setStatus("Sent!");
    try {
      await axios.post(`${process.env.REACT_APP_SERVER}/feedback/`, { data: feedback });
      setFeedback({ name: "", email: "", message: "" });
    } catch (err) {
      console.error(err);
    }
    timerRef.current = setTimeout(() => setStatus("SEND"), 5000);
  };

  return (
    <Box
      sx={{
        p: 4,
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 6,
        background: "linear-gradient(135deg, #ff9a9e, #fad0c4)",
        minHeight: "100vh",
      }}
    >
      {/* Header / Hero */}
      <Box sx={{ textAlign: "center" }}>
        <Typography variant="h2" sx={{ fontFamily: "Comic Sans MS", color: "#fff", mb: 2 }}>
          Hangman Online
        </Typography>
        <img
          src="/mnt/data/816f1d1f-86fa-403d-8445-8f2d6226891a.png"
          alt="Hangman"
          style={{ width: "200px", marginBottom: "24px" }}
        />
        <Box sx={{ display: "flex", gap: 2, justifyContent: "center" }}>
          <Button
            variant="contained"
            color="secondary"
            sx={{ fontWeight: "bold", borderRadius: 4 }}
            onClick={handleCreateRoom}
          >
            Create Room
          </Button>
          <TextField
            placeholder="Room ID"
            value={roomIdInput}
            onChange={(e) => setRoomIdInput(e.target.value)}
            sx={{ background: "#fff", borderRadius: 2 }}
          />
          <Button
            variant="contained"
            color="primary"
            sx={{ fontWeight: "bold", borderRadius: 4 }}
            onClick={handleJoinRoom}
          >
            Join Room
          </Button>
        </Box>
      </Box>

      {/* How to Play */}
      <Card sx={{ maxWidth: 800, width: "100%", borderRadius: 3, boxShadow: 6 }}>
        <CardContent>
          <Typography variant="h4" gutterBottom sx={{ color: "#f50057" }}>
            How to Play Hangman
          </Typography>
          <Typography variant="body1">
            1. One player sets a word. <br />
            2. Other players guess letters one by one. <br />
            3. Each wrong guess loses a life. <br />
            4. Complete the word before you run out of lives!
          </Typography>
        </CardContent>
      </Card>

      {/* Feedback */}
      <Card sx={{ maxWidth: 800, width: "100%", borderRadius: 3, boxShadow: 6 }}>
        <CardContent>
          <Typography variant="h4" gutterBottom sx={{ color: "#f50057" }}>
            Feedback
          </Typography>
          <form onSubmit={handleFeedbackSubmit}>
            <Grid container spacing={2}>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Name"
                  value={feedback.name}
                  onChange={(e) => setFeedback({ ...feedback, name: e.target.value })}
                  required
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Email"
                  type="email"
                  value={feedback.email}
                  onChange={(e) => setFeedback({ ...feedback, email: e.target.value })}
                  required
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Message"
                  value={feedback.message}
                  onChange={(e) => setFeedback({ ...feedback, message: e.target.value })}
                  required
                />
              </Grid>
            </Grid>
            <Button type="submit" variant="contained" color="secondary" sx={{ mt: 2 }}>
              {status}
            </Button>
          </form>
        </CardContent>
      </Card>
    </Box>
  );
};

export default Home;
