import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import Game from "./Game";
import Wait from "./Wait";
import Chat from "./Chat";
import NewWord from "./NewWord";
import axios from "axios";
import { socket } from "../modules/socket";
import { Box, Typography, Paper, List, ListItem, ListItemText, Divider } from "@mui/material";

function Room({ username, mute }) {
  const [gameState, setGameState] = useState();
  const [user, setUser] = useState(username);
  const { roomID } = useParams();
  const [err, setErr] = useState(false);

  const handleLeave = useCallback((newState) => {
    if (newState && newState.players.length === 0) setErr(true);
    setGameState({ ...newState });
  }, []);

  useEffect(() => {
    const getGameState = async () => {
      try {
        const backendURL = process.env.REACT_APP_SERVER || "http://localhost:5000";
        const res = await axios.get(`${backendURL}/?roomID=${roomID}`);

        if (res.status === 200) setGameState(res.data);
      } catch (error) {
        setErr(true);
      }
    };

    socket.on("leave", handleLeave);
    getGameState();

    return () => {
      socket.off("leave", handleLeave);
    };
  }, [roomID, handleLeave]);

  useEffect(() => {
    const cleanup = () => {
      socket.emit("leave", { user, roomID });
    };

    window.addEventListener("unload", cleanup);
    return () => window.removeEventListener("unload", cleanup);
  }, [user, roomID]);

  const renderContent = () => {
    if (err) return <Typography color="error">Room does not exist</Typography>;
    if (!gameState) return <Typography>Loading...</Typography>;

    if (user === "" || !gameState.gameStart) {
      return (
        <Wait
          user={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          setUser={setUser}
          mute={mute}
        />
      );
    }

    if (gameState.gameStart && gameState.category !== "") {
      return (
        <Game
          username={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          mute={mute}
        />
      );
    }

    if (gameState.category === "") {
      return (
        <NewWord
          gameState={gameState}
          setGameState={setGameState}
          user={user}
          roomID={roomID}
          mute={mute}
        />
      );
    }

    return <Typography>Loading...</Typography>;
  };

  const renderPlayers = () => {
    if (!gameState || !gameState.players) return null;
    return (
      <Paper sx={{ p: 2, mb: 2, backgroundColor: "#f5f5f5" }}>
        <Typography variant="h6" gutterBottom>
          Room Code: <strong>{roomID}</strong>
        </Typography>
        <Divider sx={{ mb: 1 }} />
        <Typography variant="subtitle1">Players in Room:</Typography>
        <List dense>
          {gameState.players.map((p, idx) => (
            <ListItem key={idx}>
              <ListItemText
                primary={p}
                secondary={p === user ? "You" : ""}
              />
            </ListItem>
          ))}
        </List>
      </Paper>
    );
  };

  const showChat = () => {
    if (!err && gameState && gameState.players.includes(user) && gameState.players.length > 1) {
      return <Chat user={user} roomID={roomID} />;
    }
  };

  return (
    <Box sx={{ display: "flex", flexDirection: "column", p: 2 }}>
      {renderPlayers()}
      {renderContent()}
      {showChat()}
    </Box>
  );
}

export default Room;
