import React, { useState } from "react";

function Home({ onCreateRoom, onJoinRoom }) {
  const [roomCode, setRoomCode] = useState("");

  const handleJoin = () => {
    if (!roomCode.trim()) {
      alert("Enter a valid room code!");
      return;
    }
    onJoinRoom(roomCode.trim());
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900 px-4">
      <div className="bg-gray-800 w-full max-w-md p-8 rounded-2xl shadow-xl border border-gray-700">
        
        {/* Title */}
        <h1 className="text-4xl font-bold text-center text-white">ðŸŽ® HANGMAN</h1>
        <p className="text-center text-gray-300 mt-1">Multiplayer Edition</p>

        {/* Buttons */}
        <div className="flex flex-col gap-3 mt-8">
          <button
            onClick={onCreateRoom}
            className="w-full py-3 rounded-xl bg-blue-600 hover:bg-blue-700 text-white text-lg font-semibold transition"
          >
            Create Room
          </button>

          <button
            onClick={handleJoin}
            className="w-full py-3 rounded-xl bg-purple-600 hover:bg-purple-700 text-white text-lg font-semibold transition"
          >
            Join Room
          </button>
        </div>

        {/* Room Code Input */}
        <div className="mt-4">
          <input
            type="text"
            placeholder="Enter Room Code"
            value={roomCode}
            onChange={(e) => setRoomCode(e.target.value)}
            className="w-full p-3 bg-gray-700 text-white rounded-xl border border-gray-600 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* Rules */}
        <div className="mt-8 text-gray-300">
          <h3 className="text-xl font-semibold mb-2">How to Play:</h3>
          <ul className="list-disc ml-5 space-y-1">
            <li>Create or join a room with friends</li>
            <li>Take turns guessing letters</li>
            <li>Guess the word before running out of tries</li>
            <li>Earn points for solving words</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Home;
