import React from 'react';

function PlayerList({ players, currentPlayerId, wordSetterId, currentTurnId }) {
  return (
    <div className="bg-gray-50 rounded-xl p-4 shadow space-y-2">
      <h3 className="text-lg font-bold">Players</h3>
      {players.map(player => (
        <div key={player.id} className={`flex justify-between items-center p-2 rounded border ${player.id === currentPlayerId ? 'border-indigo-500 bg-indigo-50' : player.id === currentTurnId ? 'border-yellow-400 bg-yellow-50' : 'border-gray-200'}`}>
          <span className="font-semibold">{player.name}</span>
          <div className="flex gap-1">
            {player.isHost && <span className="text-yellow-600 font-bold text-xs">üëë Host</span>}
            {player.id === wordSetterId && <span className="text-purple-600 font-bold text-xs">üìù Setter</span>}
            <span className="text-green-600 font-bold text-xs">üèÜ {player.score}</span>
            {player.strikes > 0 && player.id !== wordSetterId && <span className="text-red-600 font-bold text-xs">‚ùå {player.strikes}</span>}
          </div>
        </div>
      ))}
    </div>
  );
}

export default PlayerList;
