import React from "react";

export default function WordDisplay({ word }) {
  return (
    <div className="flex gap-2 text-3xl font-bold">
      {word
        ? word.split("").map((ch, i) => (
            <span key={i} className="border-b-2 border-gray-800 w-8 text-center">
              {ch !== "_" ? ch : ""}
            </span>
          ))
        : ""}
    </div>
  );
}
