import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import Game from "./Game";
import Wait from "./Wait";
import Chat from "./Chat";
import NewWord from "./NewWord";
import axios from "axios";
import "../css/Room.scss";
import { socket } from "../modules/socket";
import { Box, Typography, Paper, List, ListItem, ListItemText } from "@mui/material";

function Room({ username, mute }) {
  const [gameState, setGameState] = useState(null);
  const [user, setUser] = useState(username || "");
  const { roomID } = useParams();
  const [err, setErr] = useState(false);

  // Handle player leaving
  const handleLeave = useCallback((newState) => {
    if (!newState || !Array.isArray(newState.players) || newState.players.length === 0) {
      setErr(true);
    } else {
      setGameState({ ...newState });
    }
  }, []);

  // Fetch initial game state
  useEffect(() => {
    const getGameState = async () => {
      try {
        const backendURL = process.env.REACT_APP_SERVER || "http://localhost:5000";
        const res = await axios.get(`${backendURL}/?roomID=${roomID}`);
        if (res.status === 200 && res.data && res.data.players) {
          setGameState(res.data);
        } else {
          setErr(true);
        }
      } catch {
        setErr(true);
      }
    };

    socket.on("leave", handleLeave);
    getGameState();
    return () => socket.off("leave", handleLeave);
  }, [roomID, handleLeave]);

  // Handle leaving on window unload
  useEffect(() => {
    const cleanup = () => {
      if (user) socket.emit("leave", { user, roomID });
    };
    window.addEventListener("unload", cleanup);
    return () => window.removeEventListener("unload", cleanup);
  }, [user, roomID]);

  // Render game / wait / new word
  const renderContent = () => {
    if (err) return <Typography color="error">Room does not exist</Typography>;
    if (!gameState) return <Typography>Loading...</Typography>;

    if (!user || !gameState.gameStart) {
      return (
        <Wait
          user={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          setUser={setUser}
          mute={mute}
        />
      );
    }

    if (gameState.gameStart && gameState.category !== "") {
      return (
        <Game
          username={user}
          roomID={roomID}
          gameState={gameState}
          setGameState={setGameState}
          mute={mute}
        />
      );
    }

    if (gameState.category === "") {
      return (
        <NewWord
          gameState={gameState}
          setGameState={setGameState}
          user={user}
          roomID={roomID}
          mute={mute}
        />
      );
    }

    return <Typography>Loading...</Typography>;
  };

  return (
    <Box
      sx={{
        display: "flex",
        height: "95vh",
        width: "96vw",
        px: "8px",
        boxSizing: "border-box",
      }}
    >
      {/* Left Panel: Room Code + Players with points */}
      <Paper
        elevation={3}
        sx={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          p: 2,
          borderRadius: 0,
          overflowY: "auto",
        }}
      >
        <Typography
          variant="h6"
          fontWeight="bold"
          textAlign="center"
          sx={{ mb: 2 }}
        >
          ðŸŽ® Room Code: {roomID.toUpperCase()}
        </Typography>

        <Typography variant="subtitle1" fontWeight="bold" sx={{ mb: 1 }}>
          Players
        </Typography>
        <List dense>
          {gameState?.players?.map((p, idx) => (
            <ListItem key={p.name || p}>
              <ListItemText
                primary={`${idx + 1}. ${p.name || p} (${p.points || 0} pts)`}
              />
            </ListItem>
          ))}
        </List>
      </Paper>

      {/* Center Panel: Round, Word, Category + Game */}
      <Box
        sx={{
          flex: 2,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          overflow: "hidden",
          p: 2,
        }}
      >
        {/* Top Info */}
        {gameState && gameState.gameStart && (
          <Box sx={{ width: "100%", mb: 2, textAlign: "center" }}>
            <Typography variant="subtitle1">
              Round: {gameState.round || 1}
            </Typography>
            <Typography variant="h5" fontWeight="bold">
              Word: {gameState.displayWord || "_ _ _ _"}
            </Typography>
            <Typography variant="subtitle1">
              Category: {gameState.category || "N/A"}
            </Typography>
          </Box>
        )}

        {/* Game content */}
        <Box
          sx={{
            flex: 1,
            width: "100%",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          {renderContent()}
        </Box>
      </Box>

      {/* Right Panel: Chat */}
      <Paper
        elevation={3}
        sx={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          p: 2,
          borderRadius: 0,
          overflowY: "auto",
        }}
      >
        <Typography
          variant="h6"
          fontWeight="bold"
          textAlign="center"
          sx={{ mb: 2 }}
        >
          ðŸ’¬ Chat
        </Typography>
        {user && <Chat user={user} roomID={roomID} />}
      </Paper>
    </Box>
  );
}

export default Room;
